/* cell structure */
typedef struct s7_cell {
  union {
    unsigned int flag;
    unsigned char type_field;
    unsigned short sflag;
  } tf;
  int hloc;
#if DEBUGGING
  int current_alloc_line, previous_alloc_line, current_alloc_type, previous_alloc_type;
  const char *current_alloc_func, *previous_alloc_func;
#endif
  union {
    
    struct { 
      unsigned int length;
      unsigned int hash;  
      char *svalue;
      bool needs_free;

      /* extra data for symbols which always have a string name field (hash field is also used specially by symbols) */
      int accessor;
      void *accessor_data;     /* FFI-accessible */
      void *op_data;           /*   same purpose, but only s7-accessible */
    } string;
    
    union {
      s7_Int integer_value;
      s7_Double real_value;

      struct {
	s7_Int numerator;
	s7_Int denominator;
      } fraction_value;

      struct {
	s7_Double rl;
	s7_Double im;
      } complex_value;

      struct {
	s7_Int n;
	const char *name;
	int length;
      } small_value;

      unsigned long ul_value; /* these two are not used by s7 in any way */
      unsigned long long ull_value;

#if WITH_GMP
      mpz_t big_integer;
      mpq_t big_ratio;
      mpfr_t big_real;
      mpc_t big_complex;
#endif
    } number;

    s7_port_t *port;
    
    struct{
      unsigned char c;
      unsigned char up_c, down_c;
      bool alpha_p, digit_p, space_p, upper_p, lower_p;
      const char *c_name; /* actually we have room here for the name */
      int length;
    } chr;

    void *c_pointer;

    int baffle_key;
    
    struct {
      s7_Int length;
      s7_pointer *elements;
      union {
	s7_vdims_t *dim_info;
	unsigned int entries;          
      } vextra;
      s7_pointer (*hash_func)(s7_scheme *sc, s7_pointer table, s7_pointer key);
      unsigned int fill_ptr;
    } vector;
    
    c_proc_t *c_proc;     /* C functions, macros */
    
    struct {               /* pairs */ 
      s7_pointer car, cdr, ecdr, fcdr;
      unsigned int line;
      union {
	struct {
	  unsigned short data;
	  unsigned short data_index;
	} d;
	unsigned int op;
      } dat;
    } cons;

    struct {
      s7_pointer args, body, env, setter;
      int arity;
    } func;

    struct {               /* symbols */
      s7_pointer name, global_slot, local_slot, initial_slot;
      long long int id;
    } sym;

    struct {               /* slots (bindings) */
      s7_pointer sym, val, nxt, pending_value, expr;
    } slt;

    struct {               /* environments (frames) */
      s7_pointer slots, nxt;
      long long int id;
      
      /* these two fields are for some special case objects like #<unspecified> */
      const char *name;
      int len;
    } envr;

    struct {               /* counter (internal) */
      s7_pointer result, list, env;
      long long int val, len;
    } ctr;

    struct {               /* additional object types (C) */
      c_object_t *info;    /*  the "class" struct */
      void *value;         /*  the value the caller associates with the object */
      s7_pointer e;        /*   the method list, if any (open environment) */
    } c_obj;
    
    s7_continuation_t *continuation;
    
    struct {               /* call-with-exit */
      unsigned int goto_loc, op_stack_loc;
      bool active;
    } rexit;
    
    struct {               /* catch */
      unsigned int goto_loc, op_stack_loc;
      s7_pointer tag;
      s7_pointer handler;
    } rcatch; /* C++ reserves "catch" I guess */
    
    struct {               /* dynamic-wind */
      s7_pointer in, out, body;
      unsigned int state;
    } winder;
  } object;
} s7_cell;


