static void init_types(void)
{
  int i;
  for (i = 0; i < NUM_TYPES; i++) 
    {
      t_number_p[i] = false;
      t_real_p[i] = false;
      t_rational_p[i] = false;
      t_simple_p[i] = false;
      t_structure_p[i] = false;
    }
  t_number_p[T_INTEGER] = true;
  t_number_p[T_RATIO] = true;
  t_number_p[T_REAL] = true;
  t_number_p[T_COMPLEX] = true;

  t_rational_p[T_INTEGER] = true;
  t_rational_p[T_RATIO] = true;
  
  t_real_p[T_INTEGER] = true;
  t_real_p[T_RATIO] = true;
  t_real_p[T_REAL] = true;

  t_big_number_p[T_BIG_INTEGER] = true;
  t_big_number_p[T_BIG_RATIO] = true;
  t_big_number_p[T_BIG_REAL] = true;
  t_big_number_p[T_BIG_COMPLEX] = true;

  t_structure_p[T_PAIR] = true;
  t_structure_p[T_VECTOR] = true;
  t_structure_p[T_HASH_TABLE] = true;

  t_simple_p[T_NIL] = true;
  t_simple_p[T_UNIQUE] = true;
  t_simple_p[T_BOOLEAN] = true;
  t_simple_p[T_CHARACTER] = true;
  t_simple_p[T_SYMBOL] = true;
  t_simple_p[T_SYNTAX] = true;
  t_simple_p[T_C_MACRO] = true;
  t_simple_p[T_C_FUNCTION] = true;
  t_simple_p[T_C_ANY_ARGS_FUNCTION] = true;
  t_simple_p[T_C_OPT_ARGS_FUNCTION] = true;
  t_simple_p[T_C_RST_ARGS_FUNCTION] = true;
  t_simple_p[T_C_LST_ARGS_FUNCTION] = true;
  /* not completely sure about the next ones */
  t_simple_p[T_ENVIRONMENT] = true;
  t_simple_p[T_INPUT_PORT] = true;
  t_simple_p[T_OUTPUT_PORT] = true;
}

/* T_STACK, T_SLOT, T_BAFFLE, and T_COUNTER are internal (stacks, bindings, call/cc barriers, map circular list checks)
 */

#define TYPE_BITS                     8
#define typeflag(p)                   ((p)->tf.flag)
#define typesflag(p)                  ((p)->tf.sflag)
#define type(p)                       ((p)->tf.type_field)

#define is_number(P)                  t_number_p[type(P)]
#define is_integer(P)                 (type(P) == T_INTEGER)
#define is_real(P)                    t_real_p[type(P)]
#define is_rational(P)                t_rational_p[type(P)]
#define is_big_number(p)              t_big_number_p[type(p)]

#define is_simple(P)                  t_simple_p[type(P)]
#define has_structure(P)              t_structure_p[type(P)]

/* the layout of these bits does matter in several cases
 */
#define NOT_IN_HEAP -1
#define T_IMMUTABLE                   (1 << (TYPE_BITS + 0))
#define is_immutable(p)               ((typeflag(p) & T_IMMUTABLE) != 0)
#define set_immutable(p)              typeflag(p) |= T_IMMUTABLE
/* immutable means the value can't be changed via set! or bind -- this is separate from the symbol access stuff
 */
#define dont_copy(p)                  ((!is_pair(p)) || (is_immutable(p)) || (p->hloc == NOT_IN_HEAP))
/* dont_copy means the object is not copied when saved in a continuation */

#define T_SYNTACTIC                   (1 << (TYPE_BITS + 1))
#define is_syntactic(p)               ((typeflag(p) & T_SYNTACTIC) != 0)
#define SYNTACTIC_TYPE                (T_SYMBOL | T_DONT_EVAL_ARGS | T_SYNTACTIC)
#define SYNTACTIC_PAIR                (unsigned short)(T_PAIR | T_SYNTACTIC)
/* this marks symbols that represent syntax objects 
 */

#define T_PROCEDURE                   (1 << (TYPE_BITS + 2))
#define is_procedure(p)               ((typeflag(p) & T_PROCEDURE) != 0)
/* closure, c_function, settable object, goto or continuation 
 *   perhaps this should be T_APPLICABLE?
 */

#define T_OPTIMIZED                   (1 << (TYPE_BITS + 3))
#define set_optimized(p)              typeflag(p) |= T_OPTIMIZED
static int t_optimized = T_OPTIMIZED;
#define is_optimized(p)               ((typeflag(p) & t_optimized) != 0)
#define clear_optimized(p)            typeflag(p) &= ~(T_OPTIMIZED)
#define OPTIMIZED_PAIR                (unsigned short)(T_PAIR | T_OPTIMIZED)
/* optimizer flag for an expression that has optimization info
 */

#define T_ANY_MACRO                   (1 << (TYPE_BITS + 4))
#define is_any_macro(p)               ((typeflag(p) & T_ANY_MACRO) != 0)
#define is_procedure_or_macro(p)      ((typeflag(p) & (T_ANY_MACRO | T_PROCEDURE)) != 0)
/* this marks scheme and C-defined macros 
 */

#define T_DONT_EVAL_ARGS              (1 << (TYPE_BITS + 5))
#define dont_eval_args(p)             ((typeflag(p) & T_DONT_EVAL_ARGS) != 0)
/* this marks things that don't evaluate their arguments
 */

#define T_EXPANSION                   (1 << (TYPE_BITS + 6))
#define EXPANSION_TYPE                (T_EXPANSION | T_SYMBOL)
#define is_expansion(p)               (typeflag(p) == EXPANSION_TYPE)
/* this marks the symbol associated with a run-time macro and distinguishes the value from an ordinary macro
 */

#define T_GLOBAL                      (1 << (TYPE_BITS + 7))
#define is_global(p)                  ((typeflag(p) & T_GLOBAL) != 0)
/* this marks something defined (bound) at the top-level, and never defined locally */
#define set_global(p)                 typeflag(p) |= T_GLOBAL
#define set_local(p)                  typeflag(p) = (typeflag(p) & ~(T_DONT_EVAL_ARGS | T_GLOBAL | T_SYNTACTIC))

#define T_MULTIPLE_VALUE              (1 << (TYPE_BITS + 8))
#define is_multiple_value(p)          ((typeflag(p) & T_MULTIPLE_VALUE) != 0)
#define set_multiple_value(p)         typeflag(p) |= T_MULTIPLE_VALUE
#define multiple_value(p)             p
/* this bit marks a list (from "values") that is waiting for a
 *    chance to be spliced into its caller's argument list.  It is normally
 *    on only for a very short time.
 */

#define T_KEYWORD                     (1 << (TYPE_BITS + 10))
#define is_keyword(p)                 ((typeflag(p) & T_KEYWORD) != 0)
/* this bit distinguishes a symbol from a symbol that is also a keyword
 */

#define T_OVERLAY                     (1 << (TYPE_BITS + 12))
#define set_overlay(p)                typeflag(p) |= T_OVERLAY
#define is_overlaid(p)                ((typeflag(p) & T_OVERLAY) != 0)
/* optimizer flag that marks a cell whose ecdr points to the previous cell in a list
 */

#define T_SAFE_PROCEDURE              (1 << (TYPE_BITS + 13))
#define is_safe_procedure(p)          ((typeflag(p) & T_SAFE_PROCEDURE) != 0)
/* c_functions that do not return or modify the arg list directly (no :rest arg in particular),
 *    and that can't call apply themselves either directly or via s7_call.
 *    I think the latter would be safe if they gc_protect the called function.  
 * This also includes applicable objects that are known safe (lists, vectors, etc -- equivalent to vector-ref).
 */

#define T_CHECKED                     (1 << (TYPE_BITS + 14))
#define set_checked(p)                typeflag(p) |= T_CHECKED
#define is_checked(p)                 ((typeflag(p) & T_CHECKED) != 0)
#define is_not_checked(p)             ((typeflag(p) & T_CHECKED) == 0)

#define T_CHECKED_1                   (1 << (TYPE_BITS + 11))
#define set_checked_1(p)              typeflag(p) |= T_CHECKED_1
#define is_checked_1(p)               ((typeflag(p) & T_CHECKED_1) != 0)
/* optimizer flags that an expression has been checked for possible optimization 
 *    (trying to avoid circular lists during the optimization scan)
 */

#define T_UNSAFE                      (1 << (TYPE_BITS + 15))
#define set_unsafe(p)                 typeflag(p) |= T_UNSAFE
#define is_unsafe(p)                  ((typeflag(p) & T_UNSAFE) != 0)
#define clear_unsafe(p)               typeflag(p) &= (~T_UNSAFE)
/* optimizer flag saying "this expression is not completely self-contained.  It might involve the stack, etc"
 */

#define T_SAFE_CLOSURE                (1 << (TYPE_BITS + 16))
#define is_safe_closure(p)            ((typeflag(p) & T_SAFE_CLOSURE) != 0)
#define set_safe_closure(p)           typeflag(p) = (T_SAFE_CLOSURE | (typeflag(p) & (~T_COPY_ARGS)))
#define clear_safe_closure(p)         typeflag(p) = (T_COPY_ARGS | (typeflag(p) & (~T_SAFE_CLOSURE)))
/* optimizer flag for a closure body that is completely simple (every expression is safe)
 */

#define T_SETTER                      (1 << (TYPE_BITS + 17))
#define set_setter(p)                 typeflag(p) |= T_SETTER
#define is_setter(p)                  ((typeflag(p) & T_SETTER) != 0)
/* optimizer flag for a procedure that sets some variable (set-car! for example).
 */

#define T_HAS_TABLE                   (1 << (TYPE_BITS + 18))
#define set_has_table(p)              typeflag(p) |= T_HAS_TABLE
#define has_table(p)                  ((typeflag(p) & T_HAS_TABLE) != 0)
/* optimizer flag (external function using a function table for variable access)
 */

#define T_ONE_LINER                   (1 << (TYPE_BITS + 19))
#define set_one_liner(p)              typeflag(p) |= T_ONE_LINER
#define is_one_liner(p)               ((typeflag(p) & T_ONE_LINER) != 0)
/* implicit begin optimization -- I need to do this differently
 */

#define T_COPY_ARGS                   (1 << (TYPE_BITS + 20))
#define set_copy_args(p)              typeflag(p) |= T_COPY_ARGS
#define needs_copied_args(p)          ((typeflag(p) & T_COPY_ARGS) != 0)
/* this marks something that might mess with its argument list
 */

#define T_GENSYM                      (1 << (TYPE_BITS + 21))
#define is_gensym(p)                  ((typeflag(p) & T_GENSYM) != 0)
#define clear_gensym(p)               typeflag(p) = (typeflag(p) & (~T_GENSYM))
/* symbol is from gensym (GC-able etc)
 * it can be used in other (non-symbol contexts)
 */

#define T_SMALL_INT                   T_GENSYM
#define is_small_int(p)               ((typeflag(p) & T_SMALL_INT) != 0)

#define T_HAS_METHODS                 (1 << (TYPE_BITS + 22))
#define has_methods(p)                ((typeflag(p) & T_HAS_METHODS) != 0)
#define set_has_methods(p)            typeflag(p) |= T_HAS_METHODS
/* this marks an environment or closure that is "opened" up to generic functions etc */

#define T_GC_MARK                     (1 << (TYPE_BITS + 23))
#define is_marked(p)                  ((typeflag(p) &  T_GC_MARK) != 0)
/* #define is_marked(p)               (typeflag(p) >= T_GC_MARK) */ /* exactly the same speed? */ 
#define set_mark(p)                   typeflag(p) |= T_GC_MARK
#define clear_mark(p)                 typeflag(p) &= (~T_GC_MARK)
/* using bit 23 for this makes a big difference in the GC
 */

#define UNUSED_BITS                   0x00020000

