static s7_pointer g_symbol_table_is_locked(s7_scheme *sc, s7_pointer args)
{
  #define H_symbol_table_is_locked "set (-s7-symbol-table-locked?) to #t to prohibit the creation of any new symbols"
  return(make_boolean(sc, sc->symbol_table_is_locked));
}


static s7_pointer g_set_symbol_table_is_locked(s7_scheme *sc, s7_pointer args)
{
  sc->symbol_table_is_locked = (car(args) != sc->F);
  return(car(args));
}


static s7_pointer make_symbol(s7_scheme *sc, const char *name) 
{
  s7_pointer x; 
  int location;
  unsigned int loc = 0;

  location = symbol_table_hash(name, &loc); 
  x = symbol_table_find_by_name(sc, name, location); 
  if (is_not_null(x)) 
    return(x); 

  if (sc->symbol_table_is_locked)
    return(s7_error(sc, sc->ERROR, sc->NIL));

  x = new_symbol(sc, name, location); 
  symbol_hash(x) = loc;
  return(x);
} 


s7_pointer s7_make_symbol(s7_scheme *sc, const char *name) 
{ 
  if (!name) return(sc->F);
  return(make_symbol(sc, name));
}


static void remove_from_symbol_table(s7_scheme *sc, s7_pointer sym)
{
  unsigned int loc;
  s7_pointer x, y; 
  loc = symbol_hash(sym) % SYMBOL_TABLE_SIZE;
  x = vector_element(sc->symbol_table, loc);
  if (car(x) == sym)
    {
      vector_element(sc->symbol_table, loc) = cdr(x);
      free(x);
    }
  else
    {
      for (y = x, x = cdr(x); is_not_null(x); y = x, x = cdr(x))
	{
	  if (car(x) == sym)
	    {
	      cdr(y) = cdr(x);
	      free(x);
	      break;
	    }
	}
    }
}


s7_pointer s7_gensym(s7_scheme *sc, const char *prefix)
{ 
  char *name;
  int len, location;
  s7_pointer x;
  unsigned int loc = 0;
  
  len = safe_strlen(prefix) + 32;
  name = (char *)calloc(len, sizeof(char));
  /* there's no point in heroic efforts here to avoid name collisions --
   *    the user can screw up no matter what we do.
   */
  snprintf(name, len, "{%s}-%d", prefix, sc->gensym_counter++); 
  location = symbol_table_hash(name, &loc); 
  x = new_symbol(sc, name, location);  /* not T_GENSYM -- might be called from outside */
  symbol_hash(x) = loc;
  free(name);
  return(x); 
} 


static s7_pointer g_gensym(s7_scheme *sc, s7_pointer args) 
{
  #define H_gensym "(gensym (prefix \"gensym\")) returns a new, unused symbol"

  const char *prefix;
  char *name;
  int len, location;
  s7_pointer x, str, stc;
  unsigned int loc = 0;

  /* get symbol name */
  if (is_not_null(args))
    {
      if (!s7_is_string(car(args)))
	{
	  CHECK_METHOD(sc, car(args), sc->GENSYM, args);
	  return(simple_wrong_type_argument(sc, sc->GENSYM, car(args), T_STRING));
	}
      prefix = string_value(car(args));
    }
  else prefix = "gensym";
  len = safe_strlen(prefix) + 32;
  name = (char *)calloc(len, sizeof(char));
  snprintf(name, len, "{%s}-%d", prefix, sc->gensym_counter++); 
  location = symbol_table_hash(name, &loc); 
      
  /* make-string for symbol name */
  str = (s7_cell *)calloc(1, sizeof(s7_cell));
  str->hloc = NOT_IN_HEAP;
  set_type(str, T_STRING | T_IMMUTABLE);
  string_length(str) = safe_strlen(name);
  string_value(str) = name;
  string_needs_free(str) = false;

  /* allocate the symbol in the heap so GC'd when inaccessible */
  NEW_CELL(sc, x);
  symbol_name_cell(x) = str;
  set_type(x, T_SYMBOL | T_GENSYM);
  global_slot(x) = sc->NIL;
  initial_slot(x) = sc->UNDEFINED;
  local_slot(x) = sc->NIL;
  symbol_id(x) = 0;
  symbol_accessor(x) = -1;
  symbol_hash(x) = loc;

  /* place new symbol in symbol-table, but using calloc so we can easily free it (remove it from the table) in GC sweep */
  stc = (s7_cell *)calloc(1, sizeof(s7_cell));
  stc->hloc = NOT_IN_HEAP;
  car(stc) = x;
  set_type(stc, T_PAIR | T_IMMUTABLE);
  cdr(stc) = vector_element(sc->symbol_table, location);
  vector_element(sc->symbol_table, location) = stc;

  add_gensym(sc, x);
  return(x); 
}


