static s7_pointer find_environment(s7_scheme *sc, s7_pointer obj)
{
  switch (type(obj))
    {
    case T_ENVIRONMENT:
      return(obj);

    case T_CLOSURE:
    case T_CLOSURE_STAR:
      return(closure_environment(obj));

    case T_C_OBJECT:
      return(c_object_environment(obj));
    }
  return(sc->NIL);
}


static s7_pointer find_method(s7_scheme *sc, s7_pointer env, s7_pointer symbol) 
{ 
  s7_pointer x, y;

  for (x = env; is_environment(x); x = next_environment(x))
    for (y = environment_slots(x); is_slot(y); y = next_slot(y))
      if (slot_symbol(y) == symbol)
	return(slot_value(y));

  return(sc->UNDEFINED);
} 


static int environment_length(s7_scheme *sc, s7_pointer e)
{
  /* used by length, applicable_length, and some length optimizations */
  int i;
  s7_pointer p;

  if (e == sc->global_env)
    return(vector_fill_pointer(e));

  if (has_methods(e))
    {
      s7_pointer length_func;
      length_func = find_method(sc, e, sc->LENGTH);
      if (length_func != sc->UNDEFINED)
	return((int)s7_integer(s7_apply_function(sc, length_func, list_1(sc, e))));
    }

  for (i = 0, p = environment_slots(e); is_slot(p); i++, p = next_slot(p));
  return(i);
}


s7_pointer s7_make_slot(s7_scheme *sc, s7_pointer env, s7_pointer symbol, s7_pointer value) 
{ 
  s7_pointer slot;

  if ((!is_environment(env)) ||
      (env == sc->global_env))
    {
      s7_pointer ge;

      if ((sc->safety == 0) &&
	  ((is_closure(value))      ||
	   (is_closure_star(value)) ||
	   (is_macro(value))        ||
	   (is_bacro(value))))
	{
	  s7_remove_from_heap(sc, closure_args(value));
	  s7_remove_from_heap(sc, closure_body(value));
	}

      ge = sc->global_env;
      slot = permanent_slot(symbol, value);
      vector_element(ge, vector_fill_pointer(ge)++) = slot;
      if (vector_fill_pointer(ge) >= vector_length(ge))
	{
	  int i;
	  vector_length(ge) *= 2;
	  vector_elements(ge) = (s7_pointer *)realloc(vector_elements(ge), vector_length(ge) * sizeof(s7_pointer));
	  for (i = vector_fill_pointer(ge); i < vector_length(ge); i++)
	    vector_element(ge, i) = sc->NIL;
	}
      global_slot(symbol) = slot;
      if (symbol_id(symbol) == 0) /* never defined locally? */
	{
	  if (initial_slot(symbol) == sc->UNDEFINED)
	    initial_slot(symbol) = permanent_slot(symbol, value);
	  local_slot(symbol) = slot;
	  set_global(symbol);
	}
      /* symbol_id(symbol) = 0; */
    }
  else 
    {
      set_local(symbol); 

      NEW_CELL(sc, slot);
      slot_symbol(slot) = symbol;
      slot_set_value(slot, value);
      set_type(slot, T_SLOT | T_IMMUTABLE);
      next_slot(slot) = environment_slots(env);
      environment_slots(env) = slot;
      symbol_id(symbol) = frame_id(env);
      local_slot(symbol) = slot;
    }

  /* there are about the same number of frames as local variables -- this
   *   strikes me as surprising, but it holds up across a lot of code.
   */

  return(slot);
} 


static s7_pointer add_slot(s7_scheme *sc, s7_pointer variable, s7_pointer value) 
{ 
  /* this is called when it is guaranteed that there is a local environment */
  s7_pointer y;

  NEW_CELL(sc, y);
  slot_symbol(y) = variable;
  slot_set_value(y, value);
  set_type(y, T_SLOT | T_IMMUTABLE);
  next_slot(y) = environment_slots(sc->envir);
  environment_slots(sc->envir) = y;
  set_local(variable);
  symbol_id(variable) = frame_id(sc->envir);
  local_slot(variable) = y;
  return(y);
} 




/* -------- initial-environment -------- */

#define INITIAL_ENV_ENTRIES 400

static void save_initial_environment(s7_scheme *sc)
{
  /* there are ca 270 predefined functions (and another 80 or so other things): 348 at last count
   */
  int i, k = 0;
  s7_pointer x;
  s7_pointer *inits;

  sc->initial_env = (s7_pointer)calloc(1, sizeof(s7_cell));
  set_type(sc->initial_env, T_VECTOR);
  vector_length(sc->initial_env) = INITIAL_ENV_ENTRIES;
  vector_elements(sc->initial_env) = (s7_pointer *)malloc(INITIAL_ENV_ENTRIES * sizeof(s7_pointer));
  inits = vector_elements(sc->initial_env);
  s7_vector_fill(sc, sc->initial_env, sc->NIL);
  sc->initial_env->hloc = NOT_IN_HEAP;

  for (i = 0; i < vector_length(sc->symbol_table); i++) 
    for (x  = vector_element(sc->symbol_table, i); is_not_null(x); x = cdr(x)) 
      {
	s7_pointer sym, val;
	sym = car(x);
	if (is_slot(initial_slot(sym)))
	  {
	    val = slot_value(initial_slot(sym));
	    if ((is_procedure(val)) || (is_syntax(val)))
	      inits[k++] = initial_slot(sym);

	    /* (let ((begin +)) (with-environment (initial-environment) (begin 1 2))) */
	    /*
	    if (k >= INITIAL_ENV_ENTRIES)
	      {
		fprintf(stderr, "oops: overran inits\n");
		return;
	      }
	    */
	  }
      }
}


static s7_pointer g_initial_environment(s7_scheme *sc, s7_pointer args)
{
  /* add sc->initial_env bindings to the current environment */
  #define H_initial_environment "(initial-environment) establishes the original bindings of all the predefined functions"

  /* maybe this should be named unshadowed-current-environment or something -- it currently looks
   *   like it simply returns the initial env, but it actually shadows the global env entries
   *   that have changed.  
   *
   * slightly confusing:
   *    :((initial-environment) 'abs)
   *    #<undefined>
   *    :(defined? 'abs (initial-environment))
   *    #t
   * this is because initial-environment sets up a local environment of unshadowed symbols,
   *   and s7_environment_ref below only looks at the local env chain (that is, if env is not
   *   the global env, then the global env is not searched).
   */
  int i;
  s7_pointer *inits;
  s7_pointer x;

  sc->w = new_frame_in_env(sc, sc->envir);
  inits = vector_elements(sc->initial_env);

  for (i = 0; (i < INITIAL_ENV_ENTRIES) && (is_slot(inits[i])); i++)
    {
      x = slot_value(inits[i]);
      if (is_procedure(x))
	{
	  if ((!is_global(slot_symbol(inits[i]))) ||                   /* it's shadowed locally */
	      (x != slot_value(global_slot(slot_symbol(inits[i])))))   /* it's not shadowed, but has been changed globally */
	    s7_make_slot(sc, sc->w, slot_symbol(inits[i]), x);
	}
      else
	{
	  if ((is_syntax(x)) &&
	      (local_slot(slot_symbol(inits[i])) != sc->NIL))
	    s7_make_slot(sc, sc->w, slot_symbol(inits[i]), x);
	}
    }
  /* if (set! + -) then + needs to be overridden, but the local bit isn't set,
   *   so we have to check the actual values in the non-local case.
   *   (define (f x) (with-environment (initial-environment) (+ x 1))) 
   */

  x = sc->w;
  sc->w = sc->NIL;
  return(x);
}


