/* should these two augment-envs check for symbol accessors?
 */

static s7_pointer g_augment_environment_direct(s7_scheme *sc, s7_pointer args)
{
  #define H_augment_environment_direct "(augment-environment! env ...) adds its \
arguments (each a cons: symbol . value) directly to the environment env, and returns the \
environment."

  s7_pointer x, e;
  int i;

  e = car(args);
  if (is_null(e))
    e = sc->global_env;
  else
    {
      if (!is_environment(e))
	{
	  CHECK_METHOD(sc, e, sc->AUGMENT_ENVIRONMENTB, args);
	  return(wrong_type_argument_with_type(sc, sc->AUGMENT_ENVIRONMENTB, small_int(1), e, AN_ENVIRONMENT));
	}
    }
  for (i = 2, x = cdr(args); is_not_null(x); x = cdr(x), i++)
    {
      s7_pointer sym, val;
      if (!is_pair(car(x)))
	return(wrong_type_argument(sc, sc->AUGMENT_ENVIRONMENTB, make_integer(sc, i), car(x), T_PAIR));
      if (!is_symbol(caar(x)))
	return(wrong_type_argument_with_type(sc, sc->AUGMENT_ENVIRONMENTB, make_integer(sc, i), car(x), 
					     make_protected_string(sc, "a pair whose car is a symbol: '(symbol . value)")));
      sym = caar(x);
      val = cdar(x);
      if ((is_immutable(sym)) &&                            /* check for (eval 'pi (augment-environment! () '(pi . 1))) */
	  (!s7_is_equal(sc, val, s7_symbol_value(sc, sym))))
	return(wrong_type_argument_with_type(sc, sc->AUGMENT_ENVIRONMENTB, make_integer(sc, i), sym, 
					     make_protected_string(sc, "a non-contant symbol")));
    }
  
  if (e == sc->global_env)
    {
      for (x = cdr(args); is_not_null(x); x = cdr(x))
	{
	  if (is_slot(global_slot(caar(x))))
	    slot_set_value(global_slot(caar(x)), cdar(x));
	  else s7_make_slot(sc, e, caar(x), cdar(x));
	}
    }
  else
    {
      for (x = cdr(args); is_not_null(x); x = cdr(x))
	s7_make_slot(sc, e, caar(x), cdar(x));
    }
  return(e);
}


s7_pointer s7_augment_environment(s7_scheme *sc, s7_pointer e, s7_pointer bindings)
{
  s7_pointer new_e;

  if (e == sc->global_env)
    new_e = new_frame_in_env(sc, sc->NIL);
  else new_e = new_frame_in_env(sc, e);

  if (!is_null(bindings))
    {
      s7_pointer x;
      int gc_loc;
      gc_loc = s7_gc_protect(sc, new_e);

      for (x = bindings; is_not_null(x); x = cdr(x))
	s7_make_slot(sc, new_e, caar(x), cdar(x));

      s7_gc_unprotect_at(sc, gc_loc);
    }

  return(new_e);
}


/* (define (environment . args) (apply augment-environment '() args))
 * 
 * currently (eval 'a (augment-environment () '(a . 1) '(a . 2))) -> 2,
 *   but should it be an error?
 */

static s7_pointer g_augment_environment(s7_scheme *sc, s7_pointer args)
{
  #define H_augment_environment "(augment-environment env ...) adds its \
arguments (each a cons: symbol . value) to the environment env, and returns the \
new environment."

  s7_pointer e, x;
  int i;

  e = car(args);
  if (is_null(e))
    e = sc->global_env;
  else
    {
      if (!is_environment(e))
	{
	  CHECK_METHOD(sc, e, sc->AUGMENT_ENVIRONMENT, args);
	  return(wrong_type_argument_with_type(sc, sc->AUGMENT_ENVIRONMENT, small_int(1), e, AN_ENVIRONMENT));
	}
    }

  if (!is_null(cdr(args)))
    for (i = 2, x = cdr(args); is_not_null(x); x = cdr(x), i++)
      {
	s7_pointer sym, val;
	if (!is_pair(car(x)))
	  return(wrong_type_argument(sc, sc->AUGMENT_ENVIRONMENT, make_integer(sc, i), car(x), T_PAIR));
	if (!is_symbol(caar(x)))
	  return(wrong_type_argument_with_type(sc, sc->AUGMENT_ENVIRONMENT, make_integer(sc, i), car(x), 
					       make_protected_string(sc, "a pair whose car is a symbol: '(symbol . value)")));
	sym = caar(x);
	val = cdar(x);
	if ((is_immutable(sym)) &&                            /* check for (eval 'pi (augment-environment () '(pi . 1))) */
	    (!s7_is_equal(sc, val, s7_symbol_value(sc, sym))))
	  return(wrong_type_argument_with_type(sc, sc->AUGMENT_ENVIRONMENT, make_integer(sc, i), sym,
					       make_protected_string(sc, "a non-constant symbol")));
      }

  return(s7_augment_environment(sc, e, cdr(args)));
}


/* augment-environment is, it turns out, not quite the right thing for method lists -- in that case
 *   we don't want to redirect searchers from the global value which is still correct, or turn off
 *   optimization (based on safe=global).
 */


s7_pointer s7_environment_to_list(s7_scheme *sc, s7_pointer env)
{
  s7_pointer x;

  sc->w = sc->NIL;
  if (env == sc->global_env)
    {
      unsigned int i;
      for (i = 0; i < vector_fill_pointer(env); i++)
	if (is_slot(vector_element(env, i)))
	  sc->w = cons(sc, cons(sc, slot_symbol(vector_element(env, i)), slot_value(vector_element(env, i))), sc->w);
    }
  else
    {
      for (x = environment_slots(env); is_slot(x); x = next_slot(x))
	sc->w = cons(sc, cons(sc, slot_symbol(x), slot_value(x)), sc->w);
    }
  x = sc->w;
  sc->w = sc->NIL;
  return(x);
}


static s7_pointer g_environment_to_list(s7_scheme *sc, s7_pointer args)
{
  #define H_environment_to_list "(environment->list env) returns env's bindings as a list of cons's: '(symbol . value)."

  s7_pointer env;
  env = car(args);
  if (!is_environment(env))
    {
      CHECK_METHOD(sc, env, sc->ENVIRONMENT_TO_LIST, args);
      return(simple_wrong_type_argument_with_type(sc, sc->ENVIRONMENT_TO_LIST, env, AN_ENVIRONMENT));
    }
  return(s7_environment_to_list(sc, env));
}


s7_pointer s7_environment_ref(s7_scheme *sc, s7_pointer env, s7_pointer symbol)
{
  s7_pointer x, y;
  /* (let ((a 1)) ((current-environment) 'a)) 
   * ((global-environment) 'abs) 
   */

  if (env == sc->global_env)
    {
      y = global_slot(symbol);
      if (is_slot(y))
	return(slot_value(y));
      return(sc->UNDEFINED);
    }

  for (x = env; is_environment(x); x = next_environment(x))
    for (y = environment_slots(x); is_slot(y); y = next_slot(y))
      if (slot_symbol(y) == symbol)
	return(slot_value(y));

  return(sc->UNDEFINED);
}


s7_pointer s7_environment_set(s7_scheme *sc, s7_pointer env, s7_pointer symbol, s7_pointer value)
{
  s7_pointer x, y;

  if (env == sc->global_env)
    {
      y = global_slot(symbol);
      if (is_slot(y))
	{
	  slot_set_value(y, value);
	  return(value);
	}
      return(sc->UNDEFINED);
    }

  for (x = env; is_environment(x); x = next_environment(x))
    for (y = environment_slots(x); is_slot(y); y = next_slot(y))
      if (slot_symbol(y) == symbol)
	{
	  slot_set_value(y, value);
	  return(value);
	}
  return(sc->UNDEFINED);
}


static s7_pointer g_environment_set(s7_scheme *sc, s7_pointer args)
{
  /* (let ((a 1)) (set! ((current-environment) 'a) 32) a) */
  s7_pointer sym;

  sym = cadr(args);
  if (!is_symbol(sym))
    return(s7_wrong_type_arg_error(sc, "environment set!", 2, sym, "a symbol"));

  return(s7_environment_set(sc, car(args), sym, caddr(args)));
}


static s7_pointer reverse_slots(s7_scheme *sc, s7_pointer list)
{
  s7_pointer p = list, result, q;
  result = sc->NIL;

  while (is_slot(p))
    {
      q = next_slot(p);
      next_slot(p) = result;
      result = p;
      p = q;
    }
  return(result);
}


static s7_pointer environment_copy(s7_scheme *sc, s7_pointer env)
{
  if (is_environment(env))
    {
      s7_pointer x, new_e;
      int gc_loc;

      if (env == sc->global_env)   /* (copy (global-environment)) or (copy (procedure-environment abs)) etc */
	return(sc->global_env);

      /* we can't make copy handle environments-as-objects specially because the
       *   make-object function in define-class uses copy to make a new object!
       *   So if it is present, we get it here, and then there's almost surely trouble.
       */
      if (has_methods(env))
	{
	  /* mark the new env as open */
	  new_e = new_frame_in_env(sc, next_environment(env));
	  set_has_methods(new_e);
	}
      else new_e = new_frame_in_env(sc, next_environment(env));
      gc_loc = s7_gc_protect(sc, new_e);

      for (x = environment_slots(env); is_slot(x); x = next_slot(x))
	ADD_SLOT(new_e, slot_symbol(x), slot_value(x));
      environment_slots(new_e) = reverse_slots(sc, environment_slots(new_e));
      
      s7_gc_unprotect_at(sc, gc_loc);      
      return(new_e);
    }
  return(sc->NIL);
}


static s7_pointer g_global_environment(s7_scheme *sc, s7_pointer ignore)
{
  #define H_global_environment "(global-environment) returns the current top-level definitions (symbol bindings)."
  return(sc->global_env);
}

