static s7_pointer g_angle(s7_scheme *sc, s7_pointer args)
{
  #define H_angle "(angle z) returns the angle of z"
  s7_pointer x;
  /* (angle inf+infi) -> 0.78539816339745 ? */

  x = car(args);
  switch (type(x))
    {
    case T_INTEGER:
      if (integer(x) < 0)
	return(real_pi);
      return(small_int(0));

    case T_RATIO:
      if (numerator(x) < 0)
	return(real_pi);
      return(small_int(0));

    case T_REAL:
      if (is_NaN(real(x))) return(x);
      if (real(x) < 0.0)
	return(real_pi);
      return(real_zero);

    case T_COMPLEX:
      return(s7_make_real(sc, atan2(imag_part(x), real_part(x))));

    default:
      CHECK_METHOD(sc, x, sc->ANGLE, args);
      return(simple_wrong_type_argument_with_type(sc, sc->ANGLE, x, A_NUMBER));
    }
}


static s7_pointer g_rationalize(s7_scheme *sc, s7_pointer args)
{
  #define H_rationalize "(rationalize x err) returns the ratio with lowest denominator within err of x"
  s7_Double err;
  s7_pointer x;

  if (is_not_null(cdr(args)))
    {
      s7_pointer ex;
      ex = cadr(args);
      if (!s7_is_real(ex))
	{
	  CHECK_METHOD(sc, ex, sc->RATIONALIZE, args);
	  return(wrong_type_argument(sc, sc->RATIONALIZE, small_int(2), ex, T_REAL));
	}
      err = s7_number_to_real(ex);
      if (is_NaN(err))
	return(out_of_range(sc, sc->RATIONALIZE, small_int(2), cadr(args), "error term is NaN"));
      if (err < 0.0) err = -err;
    }
  else err = default_rationalize_error;

  x = car(args);
  switch (type(x))
    {
    case T_INTEGER:
      {
	s7_Int a, b, pa;
	if (err < 1.0) return(x);
	a = s7_integer(x);
	if (a < 0) pa = -a; else pa = a;
	if (err >= pa) return(small_int(0));
	b = (s7_Int)err;
	pa -= b;
	if (a < 0)
	  return(make_integer(sc, -pa));
	return(make_integer(sc, pa));
      }

    case T_RATIO:
      if (err == 0.0)
	return(x);

    case T_REAL:
      {
	s7_Double rat;
	s7_Int numer = 0, denom = 1;

	rat = s7_number_to_real(x);

	if ((is_NaN(rat)) || (isinf(rat)))
	  return(wrong_type_argument_with_type(sc, sc->RATIONALIZE, small_int(1), x, A_NORMAL_REAL));

	if (err >= s7_Double_abs(rat)) 
	  return(small_int(0));

	if ((rat > 9.2233720368548e+18) || (rat < -9.2233720368548e+18))
	  return(out_of_range(sc, sc->RATIONALIZE, small_int(1), x, "a real between most-negative-fixnum and most-positive-fixnum"));

	if ((s7_Double_abs(rat) + s7_Double_abs(err)) < 1.0e-18)
	  err = 1.0e-18;
	/* (/ 1.0 most-positive-fixnum) is 1.0842021e-19, so if we let err be less than that,
	 * (rationalize 1e-19 1e-20) hangs, but this only affects the initial ceiling, I believe.
	 */

	if (s7_Double_abs(rat) < s7_Double_abs(err))
	  return(small_int(0));

	if (c_rationalize(rat, err, &numer, &denom))
	  return(s7_make_ratio(sc, numer, denom));

	return(sc->F);
      }

    default:
      CHECK_METHOD(sc, x, sc->RATIONALIZE, args);
      return(wrong_type_argument(sc, sc->RATIONALIZE, small_int(1), x, T_REAL));
    }
}


static s7_pointer g_make_polar(s7_scheme *sc, s7_pointer args)
{
  s7_pointer x, y;
  s7_Double ang, mag;
  #define H_make_polar "(make-polar mag ang) returns a complex number with magnitude mag and angle ang"
  
  x = car(args);
  y = cadr(args);

  switch (type(x))
    {
    case T_INTEGER:
      switch (type(y))
	{
	case T_INTEGER:
	  if (integer(x) == 0) return(x);            /* (make-polar 0 1) -> 0 */
	  if (integer(y) == 0) return(x);            /* (make-polar 1 0) -> 1 */
	  mag = (s7_Double)integer(x);
	  ang = (s7_Double)integer(y);
	  break;

	case T_RATIO:
	  if (integer(x) == 0) return(x);
	  mag = (s7_Double)integer(x);
	  ang = (s7_Double)fraction(y);
	  break;

	case T_REAL:
	  ang = real(y);
	  if (ang == 0.0) return(x);
	  if (is_NaN(ang)) return(y);
	  if (isinf(ang)) return(real_NaN);
	  if ((ang == M_PI) || (ang == -M_PI)) return(make_integer(sc, -integer(x)));
	  mag = (s7_Double)integer(x);
	  break;

	default:
	  CHECK_METHOD(sc, y, sc->MAKE_POLAR, args);
	  return(wrong_type_argument(sc, sc->MAKE_POLAR, small_int(2), y, T_REAL));
	}
      break;

    case T_RATIO:
      switch (type(y))
	{
	case T_INTEGER:
	  if (integer(y) == 0) return(x);
	  mag = (s7_Double)fraction(x);
	  ang = (s7_Double)integer(y);
	  break;

	case T_RATIO:
	  mag = (s7_Double)fraction(x);
	  ang = (s7_Double)fraction(y);
	  break;

	case T_REAL:
	  ang = real(y);
	  if (ang == 0.0) return(x);
	  if (is_NaN(ang)) return(y);
	  if (isinf(ang)) return(real_NaN);
	  if ((ang == M_PI) || (ang == -M_PI)) return(s7_make_ratio(sc, -numerator(x), denominator(x)));
	  mag = fraction(x);
	  break;

	default:
	  CHECK_METHOD(sc, y, sc->MAKE_POLAR, args);
	  return(wrong_type_argument(sc, sc->MAKE_POLAR, small_int(2), y, T_REAL));
	}
      break;

    case T_REAL:
      mag = real(x);
      switch (type(y))
	{
	case T_INTEGER:
	  if (is_NaN(mag)) return(x);
	  if (integer(y) == 0) return(x);
	  ang = (s7_Double)integer(y);
	  break;

	case T_RATIO:
	  if (is_NaN(mag)) return(x);
	  ang = (s7_Double)fraction(y);
	  break;

	case T_REAL:
	  if (is_NaN(mag)) return(x);
	  ang = real(y);
	  if (ang == 0.0) return(x);
	  if (is_NaN(ang)) return(y);
	  if (isinf(ang)) return(real_NaN);
	  break;

	default:
	  CHECK_METHOD(sc, y, sc->MAKE_POLAR, args);
	  return(wrong_type_argument(sc, sc->MAKE_POLAR, small_int(2), y, T_REAL));
	}
      break;

    default:
      CHECK_METHOD(sc, x, sc->MAKE_POLAR, args);
      return(wrong_type_argument(sc, sc->MAKE_POLAR, small_int(1), x, T_REAL));
    }

  return(s7_make_complex(sc, mag * cos(ang), mag * sin(ang)));

  /* since sin is inaccurate for large arguments, so is make-polar:
   *    (make-polar 1.0 1e40) -> -0.76267273202438+0.64678458842683i, not 8.218988919070239214448025364432557517335E-1-5.696334009536363273080341815735687231337E-1i
   */
}


static s7_pointer g_make_rectangular(s7_scheme *sc, s7_pointer args)
{
  s7_pointer x, y;
  #define H_make_rectangular "(make-rectangular x1 x2) returns a complex number with real-part x1 and imaginary-part x2"
  
  x = car(args);
  y = cadr(args);

  switch (type(y))
    {
    case T_INTEGER:
      switch (type(x))
	{
	case T_INTEGER: 
	  if (integer(y) == 0) return(x); 
	  return(s7_make_complex(sc, (s7_Double)integer(x), (s7_Double)integer(y)));

	case T_RATIO:   
	  if (integer(y) == 0) return(x); 
	  return(s7_make_complex(sc, (s7_Double)fraction(x), (s7_Double)integer(y)));

	case T_REAL:    
	  if (integer(y) == 0) return(x); 
	  return(s7_make_complex(sc, real(x), (s7_Double)integer(y)));

	default:        
	  CHECK_METHOD(sc, x, sc->MAKE_RECTANGULAR, args);
	  return(wrong_type_argument(sc, sc->MAKE_RECTANGULAR, small_int(1), x, T_REAL));
	}
      
    case T_RATIO:
      switch (type(x))
	{
	case T_INTEGER: 
	  return(s7_make_complex(sc, (s7_Double)integer(x), (s7_Double)fraction(y)));

	case T_RATIO:   
	  return(s7_make_complex(sc, (s7_Double)fraction(x), (s7_Double)fraction(y)));

	case T_REAL:    
	  return(s7_make_complex(sc, real(x), (s7_Double)fraction(y)));

	default:        
	  CHECK_METHOD(sc, x, sc->MAKE_RECTANGULAR, args);
	  return(wrong_type_argument(sc, sc->MAKE_RECTANGULAR, small_int(1), x, T_REAL));
	}

    case T_REAL:
      switch (type(x))
	{
	case T_INTEGER: 
	  if (real(y) == 0.0) return(x); 
	  return(s7_make_complex(sc, (s7_Double)integer(x), real(y)));

	case T_RATIO:   
	  if (real(y) == 0.0) return(x); 
	  return(s7_make_complex(sc, (s7_Double)fraction(x), real(y)));

	case T_REAL:    
	  if (real(y) == 0.0) return(x); 
	  return(s7_make_complex(sc, real(x), real(y)));

	default:        
	  CHECK_METHOD(sc, x, sc->MAKE_RECTANGULAR, args);
	  return(wrong_type_argument(sc, sc->MAKE_RECTANGULAR, small_int(1), x, T_REAL));
	}

    default:
      CHECK_METHOD(sc, y, sc->MAKE_RECTANGULAR, args);
      return(wrong_type_argument(sc, sc->MAKE_RECTANGULAR, small_int(2), y, T_REAL));
    }
}


static s7_pointer g_exp(s7_scheme *sc, s7_pointer args)
{
  #define H_exp "(exp z) returns e^z, (exp 1) is 2.718281828459"
  s7_pointer x;

  x = car(args);
  switch (type(x))
    {
    case T_INTEGER:
      if (integer(x) == 0) return(small_int(1));                       /* (exp 0) -> 1 */
      return(s7_make_real(sc, exp((s7_Double)(integer(x)))));

    case T_RATIO:
      return(s7_make_real(sc, exp((s7_Double)fraction(x))));

    case T_REAL:
      return(s7_make_real(sc, exp(real(x))));

    case T_COMPLEX:
#if WITH_COMPLEX
      return(s7_from_c_complex(sc, cexp(as_c_complex(x))));
      /* this is inaccurate for large arguments:
       *   (exp 0+1e20i) -> -0.66491178990701-0.74692189125949i, not 7.639704044417283004001468027378811228331E-1-6.45251285265780844205811711312523007406E-1i
       */
#endif

    default:
      CHECK_METHOD(sc, x, sc->EXP, args);
      return(simple_wrong_type_argument_with_type(sc, sc->EXP, x, A_NUMBER));
    }
}

/* another possibility:
 * since (log (make-polar 1 .1)) = 0+0.1i and so on,
 *       (log (real-part (log 0)) .1) (currently = -inf-nani) = -inf+.1i
 * but   (log 0 .1) = inf-nani
 *
 * :(log (make-polar (exp 1) .1))
 *  1+0.1i
 */

static s7_pointer g_log(s7_scheme *sc, s7_pointer args)
{
  #define H_log "(log z1 (z2 e)) returns log(z1) / log(z2) where z2 (the base) defaults to e: (log 8 2) = 3"
  s7_pointer x;
  
  x = car(args);

  if (!s7_is_number(x))
    {
      CHECK_METHOD(sc, x, sc->LOG, args);
      return(wrong_type_argument_with_type(sc, sc->LOG, small_int(1), x, A_NUMBER));
    }

  if (is_pair(cdr(args)))
    {
      s7_pointer y;

      y = cadr(args);
      if (!(s7_is_number(y)))
	{
	  CHECK_METHOD(sc, y, sc->LOG, args);
	  return(wrong_type_argument_with_type(sc, sc->LOG, small_int(2), y, A_NUMBER));
	}

      if ((x == small_int(1)) && (y == small_int(1)))  /* (log 1 1) -> 0 (this is NaN in the bignum case) */
	return(small_int(0));                          

      /* (log 1 0) must be 0 since everyone says (expt 0 0) is 1 */
      if (s7_is_zero(y))
	{
	  if ((y == small_int(0)) &&
	      (x == small_int(1)))
	    return(y);
	  return(out_of_range(sc, sc->LOG, small_int(2), y, "can't be 0"));
	}

      if (s7_is_one(y))      /* this used to raise an error, but the bignum case is simpler if we return inf */
	{
	  if (s7_is_one(x))  /* but (log 1.0 1.0) -> 0.0 */
	    return(real_zero);
	  return(s7_make_real(sc, INFINITY)); /* currently (log 1/0 1) is inf? */
	}

      if ((s7_is_real(x)) &&
	  (s7_is_real(y)) &&
	  (s7_is_positive(x)) &&
	  (s7_is_positive(y)))
	{
	  if ((s7_is_rational(x)) &&
	      (s7_is_rational(y)))
	    {
	      s7_Double res;
	      s7_Int ires;
	      res = log(number_to_real(x)) / log(number_to_real(y));
	      ires = (s7_Int)res;
	      if (res - ires == 0.0)
		return(make_integer(sc, ires));   /* (log 8 2) -> 3 or (log 1/8 2) -> -3 */
	      return(s7_make_real(sc, res));         /* perhaps use rationalize here? (log 2 8) -> 1/3 */
	    }
	  return(s7_make_real(sc, log(number_to_real(x)) / log(number_to_real(y))));
	}
      return(s7_from_c_complex(sc, clog(s7_complex(x)) / clog(s7_complex(y))));
    }
  
  if (s7_is_real(x))
    {
      if (s7_is_positive(x))
	return(s7_make_real(sc, log(number_to_real(x))));
      return(s7_make_complex(sc, log(-number_to_real(x)), M_PI));
    }

  return(s7_from_c_complex(sc, clog(s7_complex(x))));
}


