static s7_pointer g_equal_length_ic(s7_scheme *sc, s7_pointer args)
{
  /* an experiment to avoid make_integer (and telescope opts)
   *   we get here with car=length expr, cadr=int
   */
  s7_Int ilen;
  s7_pointer val;
  
  val = finder(sc, cadar(args));
  ilen = s7_integer(cadr(args));

  switch (type(val))
    {
    case T_PAIR:
      return(make_boolean(sc, s7_list_length(sc, val) == ilen));

    case T_NIL:
      return(make_boolean(sc, ilen == 0));

    case T_VECTOR:
      return(make_boolean(sc, vector_length(val) == ilen));

    case T_STRING:
      return(make_boolean(sc, string_length(val) == ilen));

    case T_HASH_TABLE:
      return(make_boolean(sc, hash_table_length(val) == ilen));

    case T_C_OBJECT:
      return(make_boolean(sc, s7_integer(object_length(sc, val)) == ilen));

    case T_ENVIRONMENT:
      return(make_boolean(sc, environment_length(sc, val) == ilen));

    case T_CLOSURE:
    case T_CLOSURE_STAR:
      if (has_methods(val))
	return(make_boolean(sc, closure_length(sc, val) == ilen));

    default:
      /* here we've already lost because we've checked for the length above */
      return(simple_wrong_type_argument_with_type(sc, sc->LENGTH, val, make_protected_string(sc, "a list, vector, string, or hash-table")));
    }
  return(sc->F);
}


static s7_pointer g_equal_2(s7_scheme *sc, s7_pointer args)
{
  s7_pointer x, y;

  x = car(args);
  y = cadr(args);

  switch (type(x))
    {
    case T_INTEGER:
      switch (type(y))
	{
	case T_INTEGER: return(make_boolean(sc, integer(x) == integer(y)));
	case T_RATIO:   return(sc->F);
	case T_REAL:    return(make_boolean(sc, integer(x) == real(y)));
	case T_COMPLEX: return(sc->F);
	default:        
	  CHECK_METHOD(sc, y, sc->EQ, args);
	  return(wrong_type_argument_with_type(sc, sc->EQ, small_int(2), y, A_NUMBER));	  
	}
      break;
      
    case T_RATIO:  
      switch (type(y))
	{
	case T_INTEGER: return(sc->F);
	case T_RATIO:   return(make_boolean(sc, (numerator(x) == numerator(y)) && (denominator(x) == denominator(y))));
	case T_REAL:    return(make_boolean(sc, fraction(x) == real(y)));            /* this could avoid the divide via numerator == denominator * x */
	case T_COMPLEX: return(sc->F);
	default:        
	  CHECK_METHOD(sc, y, sc->EQ, args);
	  return(wrong_type_argument_with_type(sc, sc->EQ, small_int(2), y, A_NUMBER));	  
	}
      break;
      
    case T_REAL:    
      switch (type(y))
	{
	case T_INTEGER: return(make_boolean(sc, real(x) == integer(y)));
	case T_RATIO:   return(make_boolean(sc, real(x) == fraction(y)));
	case T_REAL:    return(make_boolean(sc, real(x) == real(y)));
	case T_COMPLEX: return(sc->F);
	default:        
	  CHECK_METHOD(sc, y, sc->EQ, args);
	  return(wrong_type_argument_with_type(sc, sc->EQ, small_int(2), y, A_NUMBER));	  
	}
      break;
      
    case T_COMPLEX:
      switch (type(y))
	{
	case T_INTEGER:
	case T_RATIO:
	case T_REAL:
	  return(sc->F);

#ifndef _MSC_VER
	case T_COMPLEX: 
	  return(make_boolean(sc, (real_part(x) == real_part(y)) && (imag_part(x) == imag_part(y))));
#else
	case T_COMPLEX: 
	  if ((real_part(x) == real_part(y)) && (imag_part(x) == imag_part(y))) return(sc->T); else return(sc->F);
#endif
	default:      
	  CHECK_METHOD(sc, y, sc->EQ, args);  
	  return(wrong_type_argument_with_type(sc, sc->EQ, small_int(2), y, A_NUMBER));	  
	}
      break;

    default:
      CHECK_METHOD(sc, x, sc->EQ, args);
      return(wrong_type_argument_with_type(sc, sc->EQ, small_int(1), x, A_NUMBER));
    }
  return(sc->F);
}

