static s7_pointer g_string_set(s7_scheme *sc, s7_pointer args)
{
  #define H_string_set "(string-set! str index chr) sets the index-th element of the string str to the character chr"
  
  s7_pointer x, index;
  char *str;
  s7_Int ind;

  x = car(args);
  index = cadr(args);
  
  if (!s7_is_string(x))
    {
      CHECK_METHOD(sc, x, sc->STRING_SET, args);
      return(wrong_type_argument(sc, sc->STRING_SET, small_int(1), x, T_STRING));
    }
  if (!s7_is_character(caddr(args)))
    {
      CHECK_METHOD(sc, caddr(args), sc->STRING_SET, args);
      return(wrong_type_argument(sc, sc->STRING_SET, small_int(3), caddr(args), T_CHARACTER));
    }
  if (!s7_is_integer(index))
    return(wrong_type_argument(sc, sc->STRING_SET, small_int(2), index, T_INTEGER));
  
  ind = s7_integer(index);

  if (ind < 0)
    return(wrong_type_argument_with_type(sc, sc->STRING_SET, small_int(2), index, make_protected_string(sc, "a non-negative integer")));
  if (ind >= string_length(x))
    return(out_of_range(sc, sc->STRING_SET, small_int(2), index, "should be less than string length"));

  str = string_value(x);
  str[ind] = (char)s7_character(caddr(args));
  return(caddr(args));
}


static s7_pointer g_string_append_1(s7_scheme *sc, s7_pointer args, s7_pointer sym)
{
  int len = 0;
  s7_pointer x, newstr;
  char *pos;
  
  if (is_null(args))
    return(s7_make_string_with_length(sc, "", 0));
  
  /* get length for new string */
  for (x = args; is_not_null(x); x = cdr(x)) 
    {
      if (!s7_is_string(car(x)))
	{
	  CHECK_METHOD(sc, car(x), sym, args);
	  return(wrong_type_argument_n(sc, sym, position_of(x, args), car(x), T_STRING));
	}
      len += string_length(car(x));
    }
  
  /* store the contents of the argument strings into the new string */
  newstr = make_empty_string(sc, len + 1, 0); 
  /* +1 here because valgrind (but only in FC15) thinks we occasionally go one past the end,
   *   but I think it'c confused by low level copy operations that do 8 bytes at a time.
   */
  string_length(newstr) = len;
  for (pos = string_value(newstr), x = args; is_not_null(x); pos += string_length(car(x)), x = cdr(x)) 
    memcpy(pos, string_value(car(x)), string_length(car(x)));
  
  return(newstr);
}


