typedef struct {
  s7_pointer table, lst;
  int loc;
} ht_iter;

static int ht_iter_tag = 0;


static char *print_ht_iter(s7_scheme *sc, void *val)
{
  char *str;
  str = (char *)calloc(32, sizeof(char));
  snprintf(str, 32, "#<hash-table-iterator>");
  return(str);
}


