/* -------------------------------- eq etc -------------------------------- */

bool s7_is_eq(s7_pointer obj1, s7_pointer obj2)
{
  return(obj1 == obj2); /* so floats and NaNs might be eq? but not eqv? */
}


bool s7_is_eqv(s7_pointer a, s7_pointer b) 
{
  if ((a == b) && (!is_number(a)))
    return(true);
  
#if WITH_GMP
  if (big_numbers_are_eqv(a, b))
    return(true);                                     /* T_INTEGER != T_BIG_INTEGER etc */
#endif

  if (type(a) != type(b)) 
    return(false);
  
  if (s7_is_string(a)) 
    return(string_value(a) == string_value(b));
  
  if (s7_is_number(a))
    return(numbers_are_eqv(a, b));
  
  return(false);
}


/* -------- structure equality -------- 
 *
 * equal? examines the entire structure (possibly a tree etc), which might contain
 *   cycles (vector element is the vector etc), so list/vector/hash-table equality
 *   needs to carry along a list of pointers seen so far.
 */

static bool structures_are_equal(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci);

static bool s7_is_equal_tracking_circles(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci)
{
  if ((x == y) && (!is_number(x))) /* this needs to be just like the g_is_equal outer test: (equal? '(1/0) '(1/0)) */
    return(true);
  
#if WITH_GMP
  if (big_numbers_are_eqv(x, y))
    return(true);
#endif

  if (type(x) != type(y)) 
    return(false);
  
  switch (type(x))
    {
    case T_STRING:
      return(scheme_strings_are_equal(x, y));

    case T_C_OBJECT:
      return(objects_are_equal(sc, x, y));

    case T_INTEGER:
    case T_RATIO:
    case T_REAL:
    case T_COMPLEX:
      return(numbers_are_eqv(x, y));

    case T_COUNTER:
      return(counter_count(x) == counter_count(y)); /* ?? not in s7_is_equal because it only occurs in a structure?? */

    case T_ENVIRONMENT:
    case T_VECTOR:
    case T_HASH_TABLE:
    case T_PAIR:
      return(structures_are_equal(sc, x, y, ci));

    case T_C_POINTER:       /* might have a list of these for example */
      return(raw_pointer(x) == raw_pointer(y));
    }

  return(false); /* we already checked that x != y (port etc) */
}


static bool hash_tables_are_equal(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci)
{
  s7_pointer *lists;
  int i, len;

  if (hash_table_entries(x) != hash_table_entries(y))
    return(false);
  if (hash_table_entries(x) == 0)
    return(true);
  if (hash_table_function(x) != hash_table_function(y))
    return(false);

  len = vector_length(x);
  lists = vector_elements(x);

  for (i = 0; i < len; i++)
    {
      s7_pointer p;
      for (p = lists[i]; is_not_null(p); p = cdr(p))
	{
	  s7_pointer key, x_val, y_val;

	  key = caar(p);
	  x_val = cdar(p);
	  y_val = (*hash_table_function(y))(sc, y, key);

	  if (is_null(y_val))
	    return(false);
	  if (!s7_is_equal_tracking_circles(sc, x_val, cdr(y_val), ci))
	    return(false);
	}
    }

  /* if we get here, every key/value in x has a corresponding key/value in y, and the number of entries match,
   *   so surely the tables are equal??
   */

  return(true);
}


static bool structures_are_equal(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci)
{
  /* here we know x and y are pointers to the same type of structure */
  int ref_x, ref_y;

  ref_x = peek_shared_ref(ci, x);
  ref_y = peek_shared_ref(ci, y);

  if ((ref_x != 0) && (ref_y != 0))
    return(ref_x == ref_y);
  
  if ((ref_x != 0) || (ref_y != 0))
    {
      /* try to harmonize the new guy -- there can be more than one structure equal to the current one */
      if (ref_x != 0)
	add_shared_ref(ci, y, ref_x);
      else add_shared_ref(ci, x, ref_y);
    }
  else add_equal_ref(ci, x, y);
  
  /* now compare the elements of the structures. */
  switch (type(x))
    {
    case T_PAIR:
      return((s7_is_equal_tracking_circles(sc, car(x), car(y), ci)) &&
	     (s7_is_equal_tracking_circles(sc, cdr(x), cdr(y), ci)));

    case T_HASH_TABLE:
      return(hash_tables_are_equal(sc, x, y, ci));

    case T_VECTOR:
      {
	s7_Int i, len;
	int x_dims = 1, y_dims = 1, j;

	len = vector_length(x);
	if (len != vector_length(y)) return(false);

	/* there's one special case: shared vectors can have 1 dimension but include the dimension info */
	if (vector_is_multidimensional(x))
	  x_dims = vector_ndims(x);
	if (vector_is_multidimensional(y))
	  y_dims = vector_ndims(y);
	    
	if (x_dims != y_dims)
	  return(false);
	    
	if (x_dims > 1)
	  for (j = 0; j < x_dims; j++)
	    if (vector_dimension(x, j) != vector_dimension(y, j))
	      return(false);
	
	for (i = 0; i < len; i++)
	  if (!(s7_is_equal_tracking_circles(sc, vector_element(x, i), vector_element(y, i), ci)))
	    return(false);
	return(true);
      }

    case T_ENVIRONMENT:
      {
	/*
	  (let ((e1 #f) (e2 #f))
	    (let ((a 1)) (set! e1 (current-environment)))
            (let ((a 1)) (set! e2 (current-environment))) 
            (equal? e1 e2))
	*/
	s7_pointer ex, ey;

	for (ex = x, ey = y; is_environment(ex) && is_environment(ey); ex = next_environment(ex), ey = next_environment(ey))
	  {
	    s7_pointer px, py;

	    if (ex == ey)
	      return(true);

	    if ((ex == sc->global_env) ||
		(ey == sc->global_env))
	      return(false);

	    /* currently order matters, unlike hash-tables */
	    for (px = environment_slots(ex), py = environment_slots(ey); is_slot(px) && is_slot(py); px = next_slot(px), py = next_slot(py))
	      if ((slot_symbol(px) != slot_symbol(py)) ||
		  (!(s7_is_equal_tracking_circles(sc, slot_value(px), slot_value(py), ci))))
		return(false);

	    if ((is_slot(px)) ||
		(is_slot(py)))
	      return(false);
	  }
	if ((is_environment(ex)) ||
	    (is_environment(ey)))
	  return(false);

	return(true);
      }
    }
  return(true);
}


bool s7_is_equal(s7_scheme *sc, s7_pointer x, s7_pointer y)
{
  if ((x == y) && (!is_number(x)))  /* (equal? 1/0 1/0) should be #f */
    return(true);

#if WITH_GMP
  if (big_numbers_are_eqv(x, y))
    return(true);
#endif

  if (type(x) != type(y)) 
    return(false);
  
  switch (type(x))
    {
    case T_UNIQUE: 
      /* we checked x == y above, and know x and y are T_UNIQUE
       */
      if (x == sc->UNSPECIFIED) return(y == sc->NO_VALUE);
      if (x == sc->NO_VALUE) return(y == sc->UNSPECIFIED);
      return(false);

    case T_STRING:
      return(scheme_strings_are_equal(x, y));

    case T_C_OBJECT:
      return(objects_are_equal(sc, x, y));

    case T_INTEGER:
    case T_RATIO:
    case T_REAL:
    case T_COMPLEX:
      return(numbers_are_eqv(x, y));

    case T_ENVIRONMENT:
      if (has_methods(x))
	{
	  if (has_methods(y))
	    {
	      s7_pointer equal_func;
	      equal_func = find_method(sc, x, sc->EQUALP);
	      if (equal_func != sc->UNDEFINED)
		return(s7_boolean(sc, s7_apply_function(sc, equal_func, list_2(sc, x, y))));
	    }
	  else return(false);
	}
      return(structures_are_equal(sc, x, y, new_shared_info(sc)));

    case T_CLOSURE:
    case T_CLOSURE_STAR:
      if ((has_methods(x)) &&
	  (has_methods(y)))
	{
	  s7_pointer equal_func;
	  equal_func = find_method(sc, closure_environment(x), sc->EQUALP);
	  if (equal_func != sc->UNDEFINED)
	    return(s7_boolean(sc, s7_apply_function(sc, equal_func, list_2(sc, x, y))));
	}
      return(false);

    case T_VECTOR:
      return((vector_length(x) == vector_length(y)) &&
	     (structures_are_equal(sc, x, y, new_shared_info(sc))));

    case T_HASH_TABLE:
      return(hash_tables_are_equal(sc, x, y, new_shared_info(sc)));

    case T_PAIR:
#if (!WITH_GMP)
      return((type(car(x)) == type(car(y))) &&
	     (structures_are_equal(sc, x, y, new_shared_info(sc))));
#else
      return(structures_are_equal(sc, x, y, new_shared_info(sc)));
#endif

    case T_C_POINTER:
      return(raw_pointer(x) == raw_pointer(y));
    }
  return(false); /* we already checked that x != y (port etc) */
}


static s7_pointer g_is_eq(s7_scheme *sc, s7_pointer args)
{
  #define H_is_eq "(eq? obj1 obj2) returns #t if obj1 is eq to (the same object as) obj2"
  return(make_boolean(sc, car(args) == cadr(args)));
}


static s7_pointer g_is_eqv(s7_scheme *sc, s7_pointer args)
{
  #define H_is_eqv "(eqv? obj1 obj2) returns #t if obj1 is equivalent to obj2"
  return(make_boolean(sc, s7_is_eqv(car(args), cadr(args))));
}


static s7_pointer g_is_equal(s7_scheme *sc, s7_pointer args)
{
  #define H_is_equal "(equal? obj1 obj2) returns #t if obj1 is equal to obj2"
  return(make_boolean(sc, s7_is_equal(sc, car(args), cadr(args))));
}


static bool hash_tables_are_morally_equal(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci)
{
  s7_pointer *lists;
  int i, len;

  if (hash_table_entries(x) != hash_table_entries(y))
    return(false);
  if (hash_table_entries(x) == 0)
    return(true);
  /* can't precheck the function here (2 != 2.0) */

  len = vector_length(x);
  lists = vector_elements(x);

  for (i = 0; i < len; i++)
    {
      s7_pointer p;
      for (p = lists[i]; is_not_null(p); p = cdr(p))
	{
	  s7_pointer key, x_val, y_val;

	  key = caar(p);
	  x_val = cdar(p);
	  y_val = (*hash_table_function(y))(sc, y, key);

	  if (is_null(y_val))
	    return(false);
	  if (!s7_is_morally_equal_1(sc, x_val, cdr(y_val), ci))
	    return(false);
	}
    }

  /* if we get here, every key/value in x has a corresponding key/value in y, and the number of entries match,
   *   so surely the tables are equal??
   */

  return(true);
}


static bool structures_are_morally_equal(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci)
{
  /* here we know x and y are pointers to the same type of structure */
  int ref_x, ref_y;

  ref_x = peek_shared_ref(ci, x);
  ref_y = peek_shared_ref(ci, y);

  if ((ref_x != 0) && (ref_y != 0))
    return(ref_x == ref_y);
  
  if ((ref_x != 0) || (ref_y != 0))
    {
      /* try to harmonize the new guy -- there can be more than one structure equal to the current one */
      if (ref_x != 0)
	add_shared_ref(ci, y, ref_x);
      else add_shared_ref(ci, x, ref_y);
    }
  else add_equal_ref(ci, x, y);
  
  /* now compare the elements of the structures. */
  switch (type(x))
    {
    case T_PAIR:
      return((s7_is_morally_equal_1(sc, car(x), car(y), ci)) &&
	     (s7_is_morally_equal_1(sc, cdr(x), cdr(y), ci)));

    case T_HASH_TABLE:
      return(hash_tables_are_morally_equal(sc, x, y, ci));

    case T_VECTOR:
      {
	s7_Int i, len;
	int x_dims = 1, y_dims = 1, j;

	len = vector_length(x);
	if (len != vector_length(y)) return(false);
	if (len == 0) return(true);

	if (vector_is_multidimensional(x))
	  x_dims = vector_ndims(x);
	if (vector_is_multidimensional(y))
	  y_dims = vector_ndims(y);
	    
	if (x_dims != y_dims)
	  return(false);
	    
	if (x_dims > 1)
	  for (j = 0; j < x_dims; j++)
	    if (vector_dimension(x, j) != vector_dimension(y, j))
	      return(false);
	
	for (i = 0; i < len; i++)
	  if (!(s7_is_morally_equal_1(sc, vector_element(x, i), vector_element(y, i), ci)))
	    return(false);
	return(true);
      }

    case T_ENVIRONMENT:
      {
	/*
	  (let ((e1 #f) (e2 #f))
	    (let ((a 1)) (set! e1 (current-environment)))
            (let ((a 1)) (set! e2 (current-environment))) 
            (equal? e1 e2))
	*/
	s7_pointer ex, ey;
	for (ex = x, ey = y; is_environment(ex) && is_environment(ey); ex = next_environment(ex), ey = next_environment(ey))
	  {
	    s7_pointer px, py;
	    if (ex == ey)
	      return(true);
	    if ((ex == sc->global_env) ||
		(ey == sc->global_env))
	      return(false);
	    /* currently order matters, unlike hash-tables */
	    for (px = environment_slots(ex), py = environment_slots(ey); is_slot(px) && is_slot(py); px = next_slot(px), py = next_slot(py))
	      if ((slot_symbol(px) != slot_symbol(py)) ||
		  (!(s7_is_morally_equal_1(sc, slot_value(px), slot_value(py), ci))))
		return(false);
	    if ((is_slot(px)) ||
		(is_slot(py)))
	      return(false);
	  }
	if ((is_environment(ex)) ||
	    (is_environment(ey)))
	  return(false);
	return(true);
      }
    }
  return(true);
}


static bool s7_is_morally_equal_1(s7_scheme *sc, s7_pointer x, s7_pointer y, shared_info *ci)
{
  #define FLOATING_EPSILON 1e-15

  if (x == y) 
    return(true);

  if ((type(x) != type(y)) &&
      (!is_number(x)) && 
      (!is_big_number(x)))
    return(false);

  switch (type(x))
    {
    case T_UNIQUE: 
      /* we checked x == y above, and know x and y are T_UNIQUE
       */
      if (x == sc->UNSPECIFIED) return(y == sc->NO_VALUE);
      if (x == sc->NO_VALUE) return(y == sc->UNSPECIFIED);
      return(false);

    case T_STRING:
      return(scheme_strings_are_equal(x, y));

    case T_C_OBJECT:
      return(objects_are_equal(sc, x, y)); /* perhaps a slot for this? */

    case T_C_POINTER:
      return(raw_pointer(x) == raw_pointer(y));

    case T_INPUT_PORT:
      return((port_is_closed(x)) &&     /* closed ports of same type are morally equal */
	     (port_type(x) == port_type(y)) &&
	     (port_is_closed(y)));
  
    case T_OUTPUT_PORT:
      return((port_is_closed(x)) &&
	     (port_type(x) == port_type(y)) &&
	     (port_is_closed(y)));

    case T_HASH_TABLE:
      return(hash_tables_are_morally_equal(sc, x, y, (ci) ? ci : new_shared_info(sc))); 

    case T_ENVIRONMENT:
      if (has_methods(x))
	{
	  if (has_methods(y))
	    {
	      s7_pointer equal_func;
	      equal_func = find_method(sc, x, sc->MORALLY_EQUALP);
	      if (equal_func != sc->UNDEFINED)
		return(s7_boolean(sc, s7_apply_function(sc, equal_func, list_2(sc, x, y))));
	    }
	  else return(false);
	}
      /* if the environment contains a function whose environment is the current environment, infinite loop?
       *    so we pass the ci arg in the macro/function cases below
       */
      return(structures_are_morally_equal(sc, x, y, (ci) ? ci : new_shared_info(sc)));

    case T_VECTOR:
      if (vector_length(x) == vector_length(y))
	{
	  if (vector_length(x) == 0)   /* all empty vectors are morally equal */
	    return(true);
	  return(structures_are_morally_equal(sc, x, y, (ci) ? ci : new_shared_info(sc)));
	}
      return(false);

    case T_PAIR:
      return(structures_are_morally_equal(sc, x, y, (ci) ? ci : new_shared_info(sc)));

#if WITH_GMP
      /* not sure how to handle these cases -- what should the "float-epsilon" be?
       */
    case T_BIG_INTEGER:
    case T_BIG_RATIO:
    case T_BIG_REAL:
    case T_BIG_COMPLEX:
      return(big_equal(sc, list_2(sc, x, y)) != sc->F);
#endif

    case T_INTEGER:
      switch (type(y))
	{
#if WITH_GMP
	case T_BIG_INTEGER:
	case T_BIG_REAL:
	case T_BIG_COMPLEX:
	case T_BIG_RATIO:
	  return(big_equal(sc, list_2(sc, x, y)) != sc->F);
#endif
	  
	case T_INTEGER:
	  return(integer(x) == integer(y));

	case T_RATIO:
	  return(fabs(integer(x) - fraction(y)) <= FLOATING_EPSILON);

	case T_REAL:
	  return((!is_NaN(real(y))) &&
		 (fabs(integer(x) - real(y)) <= FLOATING_EPSILON));

	case T_COMPLEX:
	  return((!is_NaN(real_part(y))) &&
		 (!is_NaN(imag_part(y))) &&
		 (fabs(integer(x) - real_part(y)) <= FLOATING_EPSILON) &&
		 (fabs(imag_part(y)) <= FLOATING_EPSILON));

	default:
	  return(false);
	}

    case T_RATIO:
      switch (type(y))
	{
#if WITH_GMP
	case T_BIG_INTEGER:
	case T_BIG_REAL:
	case T_BIG_COMPLEX:
	case T_BIG_RATIO:
	  return(big_equal(sc, list_2(sc, x, y)) != sc->F);
#endif
	  
	case T_INTEGER:
	  return(fabs(fraction(x) - integer(y)) <= FLOATING_EPSILON);

	case T_RATIO:
	  return(fabs(fraction(x) - fraction(y)) <= FLOATING_EPSILON);

	case T_REAL:
	  return((!is_NaN(real(y))) &&
		 (fabs(fraction(x) - real(y)) <= FLOATING_EPSILON));

	case T_COMPLEX:
	  return((!is_NaN(real_part(y))) &&
		 (!is_NaN(imag_part(y))) &&
		 (fabs(fraction(x) - real_part(y)) <= FLOATING_EPSILON) &&
		 (fabs(imag_part(y)) <= FLOATING_EPSILON));

	default:
	  return(false);
	}

    case T_REAL:
      switch (type(y))
	{
#if WITH_GMP
	case T_BIG_INTEGER:
	case T_BIG_REAL:
	case T_BIG_COMPLEX:
	case T_BIG_RATIO:
	  return(big_equal(sc, list_2(sc, x, y)) != sc->F);
#endif
	  
	case T_INTEGER:
	  return((!is_NaN(real(x))) &&
		 (fabs(real(x) - integer(y)) <= FLOATING_EPSILON));

	case T_RATIO:
	  return((!is_NaN(real(x))) &&
		 (fabs(real(x) - fraction(y)) <= FLOATING_EPSILON));

	case T_REAL:
	  if (is_NaN(real(x)))
	    return(is_NaN(real(y)));

	  return((!is_NaN(real(y))) &&
		 ((real(x) == real(y)) || /* might be inf! */
		  (fabs(real(x) - real(y)) <= FLOATING_EPSILON)));

	case T_COMPLEX:
	  if (is_NaN(real(x)))
	    return((is_NaN(real_part(y))) &&
		   (fabs(imag_part(y)) <= FLOATING_EPSILON));

	  return((!is_NaN(real(x))) &&
		 (!is_NaN(real_part(y))) &&
		 (!is_NaN(imag_part(y))) &&
		 ((real(x) == real_part(y)) ||
		  (fabs(real(x) - real_part(y)) <= FLOATING_EPSILON)) &&
		 (fabs(imag_part(y)) <= FLOATING_EPSILON));

	default:
	  return(false);
	}

    case T_COMPLEX:
      switch (type(y))
	{
#if WITH_GMP
	case T_BIG_INTEGER:
	case T_BIG_REAL:
	case T_BIG_COMPLEX:
	case T_BIG_RATIO:
	  return(big_equal(sc, list_2(sc, x, y)) != sc->F);
#endif
	  
	case T_INTEGER:
	  return((!is_NaN(real_part(x))) &&
		 (!is_NaN(imag_part(x))) &&
		 (fabs(real_part(x) - integer(y)) <= FLOATING_EPSILON) &&
		 (fabs(imag_part(x)) <= FLOATING_EPSILON));

	case T_RATIO:
	  return((!is_NaN(real_part(x))) &&
		 (!is_NaN(imag_part(x))) &&
		 (fabs(real_part(x) - fraction(y)) <= FLOATING_EPSILON) &&
		 (fabs(imag_part(x)) <= FLOATING_EPSILON));

	case T_REAL:
	  if ((is_NaN(real_part(x))) ||
	      (is_NaN(imag_part(x))) ||
	      (is_NaN(real(y))))
	    return(false);
	  return(((real_part(x) == real(y)) ||
		  (fabs(real_part(x) - real(y)) <= FLOATING_EPSILON)) &&
		 (fabs(imag_part(x)) <= FLOATING_EPSILON));

	case T_COMPLEX:
	  if (is_NaN(real_part(x)))
	    return((is_NaN(real_part(y))) &&
		   (((is_NaN(imag_part(x))) && (is_NaN(imag_part(y)))) ||
		    (imag_part(x) == imag_part(y)) ||
		    (fabs(imag_part(x) - imag_part(y)) <= FLOATING_EPSILON)));

	  if (is_NaN(imag_part(x)))
	    return((is_NaN(imag_part(y))) &&
		   ((real_part(x) == real_part(y)) ||
		    (fabs(real_part(x) - real_part(y)) <= FLOATING_EPSILON)));
		   
	  if ((is_NaN(real_part(y))) ||
	      (is_NaN(imag_part(y))))
	    return(false);

	  return(((real_part(x) == real_part(y)) ||
		  (fabs(real_part(x) - real_part(y)) <= FLOATING_EPSILON)) &&
		 ((imag_part(x) == imag_part(y)) ||
		  (fabs(imag_part(x) - imag_part(y)) <= FLOATING_EPSILON)));

	default:
	  return(false);
	}

    case T_CLOSURE:
    case T_CLOSURE_STAR:
      if (type(x) != type(y))
	return(false);
      if ((has_methods(x)) &&
	  (has_methods(y)))
	{
	  s7_pointer equal_func;
	  equal_func = find_method(sc, closure_environment(x), sc->MORALLY_EQUALP);
	  if (equal_func != sc->UNDEFINED)
	    return(s7_boolean(sc, s7_apply_function(sc, equal_func, list_2(sc, x, y))));
	}
      return((s7_is_morally_equal_1(sc, closure_args(x), closure_args(y), ci)) &&
	     (s7_is_morally_equal_1(sc, closure_environment(x), closure_environment(y), ci)) &&
	     (s7_is_morally_equal_1(sc, closure_body(x), closure_body(y), ci)));

    case T_MACRO:
    case T_BACRO:
      /* these are hard to check because the body has the gensymmed arg name
       *
       * args:   ({defmac}-16) 
       *         ({defmac}-17)
       * bodies: ((apply (lambda (a) ({list} '+ 1 a)) (cdr {defmac}-16))) 
       *         ((apply (lambda (a) ({list} '+ 1 a)) (cdr {defmac}-17)))
       * 
       * the "args" and "body" are not relevant: we create these shells internally, so the true args are in the applied lambda
       * so ignore args, and check (cadr (car body)) => (lambda (a) ({list} '+ 1 a))
       *
       * if the "ci" arg is omitted, this hangs:
       *    (morally-equal? 
       *      (let () 
       *        (define-macro* (a_func (an_arg (lambda () #t))) 
       *          `,an_arg) 
       *        (a_func)) 
       *      (let () 
       *        (define-macro (a_func an_arg) 
       *          `,an_arg) 
       *        (a_func (lambda () #t))))
       */
      return((type(y) == type(x)) &&
	     (s7_is_morally_equal_1(sc, closure_environment(x), closure_environment(y), ci)) &&
	     (s7_is_morally_equal_1(sc, cadr(car(closure_body(x))), cadr(car(closure_body(y))), ci)));

    }
  return(false);
}


static s7_pointer g_is_morally_equal(s7_scheme *sc, s7_pointer args)
{
  #define H_is_morally_equal "(morally-equal? obj1 obj2) returns #t if obj1 is close enough to obj2."
  return(make_boolean(sc, s7_is_morally_equal_1(sc, car(args), cadr(args), NULL)));
}




/* ---------------------------------------- length, copy, fill ---------------------------------------- */

static s7_pointer g_length(s7_scheme *sc, s7_pointer args)
{
  #define H_length "(length obj) returns the length of obj, which can be a list, vector, string, or hash-table. \
The length of a dotted list does not include the final cdr, and is returned as a negative number.  A circular \
list has infinite length."

  s7_pointer lst;
  lst = car(args);

  switch (type(lst))
    {
    case T_PAIR:
      {
	int len;
	len = s7_list_length(sc, lst);
	/* len < 0 -> dotted and (abs len) is length not counting the final cdr
	 * len == 0, circular so length is infinite
	 */
	if (len == 0)
	  return(s7_make_real(sc, INFINITY));
	return(make_integer(sc, len));
      }

    case T_NIL:
      return(small_int(0));

    case T_VECTOR:
      return(make_integer(sc, vector_length(lst)));

    case T_STRING:
      return(make_integer(sc, string_length(lst)));

    case T_HASH_TABLE:
      return(make_integer(sc, hash_table_length(lst)));

    case T_C_OBJECT:
      return(object_length(sc, lst));

    case T_ENVIRONMENT:
      return(make_integer(sc, environment_length(sc, lst)));

    case T_CLOSURE:
    case T_CLOSURE_STAR:
      if (has_methods(lst))
	return(make_integer(sc, closure_length(sc, lst)));

    default:
      return(simple_wrong_type_argument_with_type(sc, sc->LENGTH, lst, make_protected_string(sc, "a list, vector, string, or hash-table")));
    }
  
  return(small_int(0));
}

