static s7_pointer g_reverse(s7_scheme *sc, s7_pointer args)
{
  #define H_reverse "(reverse lst) returns a list with the elements of lst in reverse order.  reverse \
also accepts a string or vector argument."
  s7_pointer p, np;
  
  p = car(args);
  np = sc->NIL;

  switch (type(p))
    {
    case T_NIL:
      return(sc->NIL);

    case T_PAIR:
      return(s7_reverse(sc, p));
      /* if (is_null(np)) return(simple_wrong_type_argument_with_type(sc, sc->REVERSE, p, A_PROPER_LIST)); */
      break;

    case T_STRING:
      {
	int i, j, len;
	len = string_length(p);
	np = make_empty_string(sc, len, 0);
	if (len > 0)
	  for (i = 0, j = len - 1; i < len; i++, j--)
	    string_value(np)[i] = string_value(p)[j];
      }
      break;

    case T_VECTOR:
      {
	s7_Int i, j, len;
	len = vector_length(p);
	if (vector_is_multidimensional(p))
	  np = g_make_vector(sc, list_1(sc, g_vector_dimensions(sc, list_1(sc, p))));
	else np = make_vector_1(sc, len, NOT_FILLED, true);
	if (len > 0)
	  for (i = 0, j = len - 1; i < len; i++, j--)
	    vector_element(np, i) = vector_element(p, j);
      }
      break;

    case T_HASH_TABLE:
      return(hash_table_reverse(sc, p));

    case T_C_OBJECT:
      return(object_reverse(sc, p));

    default:
      CHECK_METHOD(sc, p, sc->REVERSE, args);
      return(simple_wrong_type_argument_with_type(sc, sc->REVERSE, p, make_protected_string(sc, "a list, string, vector, or hash-table")));
    }
  
  return(np);
}



static s7_pointer list_fill(s7_scheme *sc, s7_pointer obj, s7_pointer val)
{
  /* ambiguous ("tree-fill"?) but if it's like vector-fill, we just stomp on the top level */
  s7_pointer x, y;

  x = obj;
  y = obj;

  while (true)
    {
      if (!is_pair(x)) return(val);
      car(x) = val;
      if (is_pair(cdr(x)))
	{
	  x = cdr(x);
	  car(x) = val;
	  if (is_pair(cdr(x)))
	    {
	      x = cdr(x);
	      y = cdr(y);
	      if (x == y) return(val);
	    }
	  else
	    {
	      if (is_not_null(cdr(x)))
		cdr(x) = val;
	      return(val);
	    }
	}
      else
	{
	  if (is_not_null(cdr(x)))
	    cdr(x) = val;
	  return(val);
	}
    }
  return(val);
}


static s7_pointer g_fill(s7_scheme *sc, s7_pointer args)
{
  #define H_fill "(fill! obj val) fills obj with the value val"
  s7_pointer p;
  
  p = car(args);
  switch (type(p))
    {
    case T_STRING:
      return(g_string_fill(sc, args)); /* redundant type check here and below */

    case T_HASH_TABLE:
      if (is_not_null(cadr(args)))
	return(wrong_type_argument(sc, sc->FILL, small_int(2), cadr(args), T_NIL));
      return(hash_table_clear(sc, p));

    case T_VECTOR:
      return(g_vector_fill(sc, args));

    case T_C_OBJECT:
      if (c_object_fill(p))
	return((*(c_object_fill(p)))(sc, p, cadr(args)));
      return(eval_error(sc, "attempt to fill ~S?", p));

    case T_PAIR:
      return(list_fill(sc, p, cadr(args)));

    case T_NIL:
      return(cadr(args));        /* this parallels the empty vector case */

    default:
      CHECK_METHOD(sc, p, sc->FILL, args);
    }

  return(wrong_type_argument_with_type(sc, sc->FILL, small_int(1), p, make_protected_string(sc, "a fillable object"))); /* (fill! 1 0) */
}



#define SAVE_X_Y_Z(X, Y, Z)	     \
  do {                               \
    X = ((is_null(sc->x)) ? -1 : s7_gc_protect(sc, sc->x));	\
    Y = ((is_null(sc->y)) ? -1 : s7_gc_protect(sc, sc->y));	\
    Z = ((is_null(sc->z)) ? -1 : s7_gc_protect(sc, sc->z));	\
  } while (0)

#define RESTORE_X_Y_Z(X, Y, Z)                \
  do {                                        \
    if (X == -1) sc->x = sc->NIL; else {sc->x = s7_gc_protected_at(sc, X); s7_gc_unprotect_at(sc, X);} \
    if (Y == -1) sc->y = sc->NIL; else {sc->y = s7_gc_protected_at(sc, Y); s7_gc_unprotect_at(sc, Y);} \
    if (Z == -1) sc->z = sc->NIL; else {sc->z = s7_gc_protected_at(sc, Z); s7_gc_unprotect_at(sc, Z);} \
    } while (0)


static s7_pointer object_to_list(s7_scheme *sc, s7_pointer obj)
{
  switch (type(obj))
    {
    case T_VECTOR:
      return(s7_vector_to_list(sc, obj));

    case T_STRING:
      return(s7_string_to_list(sc, string_value(obj), string_length(obj)));

    case T_HASH_TABLE:
      {
	s7_pointer x, iterator;
	int gc_iter;
	/* (format #f "~{~A ~}" (hash-table '(a . 1) '(b . 2))) */

	iterator = g_make_hash_table_iterator(sc, list_1(sc, obj));
	gc_iter = s7_gc_protect(sc, iterator);

	sc->w = sc->NIL;
	while (true)
	  {
	    x = hash_table_iterate(sc, iterator);
	    if (is_null(x)) break;
	    sc->w = cons(sc, x, sc->w);
	  }

	x = sc->w;
	sc->w = sc->NIL;
	s7_gc_unprotect_at(sc, gc_iter);
	return(x);
      }
      
    case T_ENVIRONMENT:
      return(s7_environment_to_list(sc, obj));

    case T_C_OBJECT:
      {
	long int i, len; /* the "long" matters on 64-bit machines */
	s7_pointer x, z, result;
	int save_x = -1, save_y = -1, save_z = -1, gc_res = -1, gc_z = -1;
	/* (format #f "~{~A ~}" (vct 1 2 3)) */

	x = object_length(sc, obj);
	if (s7_is_integer(x))
	  len = s7_integer(x);
	else return(sc->F);

	if (len < 0)
	  return(sc->F);
	if (len == 0)
	  return(sc->NIL);

	result = g_make_list(sc, list_1(sc, make_integer(sc, len)));
	gc_res = s7_gc_protect(sc, result);
	z = list_1(sc, sc->F);
	gc_z = s7_gc_protect(sc, z);

	for (i = 0, x = result; i < len; i++, x = cdr(x))
	  {
	    car(z) = make_integer(sc, i);
	    SAVE_X_Y_Z(save_x, save_y, save_z);
	    car(x) = (*(c_object_ref(obj)))(sc, obj, z);
	    RESTORE_X_Y_Z(save_x, save_y, save_z);
	  }
	
	s7_gc_unprotect_at(sc, gc_z);
	s7_gc_unprotect_at(sc, gc_res);
	return(result);
      }
    }
  return(obj);
}



/* -------- error handlers -------- */

static const char *make_type_name(const char *name, int article)
{
  static char *typnam = NULL;
  static int typnam_len = 0;
  static const char *articles[3] =      {"", "the ", "a "};
  int len;

  len = safe_strlen(name) + 8;
  if (len > typnam_len)
    {
      if (typnam) free(typnam);
      typnam = (char *)calloc(len, sizeof(char));
      typnam_len = len;
    }
  if ((article == INDEFINITE_ARTICLE) &&
      ((name[0] == 'a') || (name[0] == 'e') || (name[0] == 'i') || (name[0] == 'o') || (name[0] == 'u')))
    strcpy(typnam, "an ");
  else strcpy(typnam, articles[article]);
  strcat(typnam, name);
  return(typnam);
}


