static bool sequence_is_safe_for_opteval(s7_scheme *sc, s7_pointer body)
{
  s7_pointer p;
  for (p = body; is_pair(p); p = cdr(p))
    {
      s7_pointer expr;
      expr = car(p);
      
      /* only pairs have the optimized bit set
	if ((!is_pair(expr)) || 
	  (!is_optimized(expr)))
	return(false);
      */
      if (!is_optimized(expr))
	return(false);

      /* we're checking twice in some cases, but the second (later) check can't be
       *   omitted, (by using is_checked), because the function might 
       *   have been redefined since the initial check.  
       */
      if ((optimize_data(expr) & 1) != 1)
	{
	  switch (optimize_data(expr))
	    {
	    case OP_VECTOR_C:
	    case OP_VECTOR_S:
	    case OP_STRING_C:
	    case OP_STRING_S:
	    case OP_PAIR_C:
	    case OP_PAIR_S:
	    case OP_HASH_TABLE_C:
	    case OP_HASH_TABLE_S:
	    case OP_C_OBJECT_C:
	    case OP_C_OBJECT_S:
	    case OP_ENVIRONMENT_S:
	      break;
	      
	    case OP_SAFE_C_C: 
	    case OP_SAFE_C_S: 
	    case OP_SAFE_C_Q:
	    case OP_SAFE_C_SS:
	    case OP_SAFE_C_SC:
	    case OP_SAFE_C_CS:
	    case OP_SAFE_C_SQ:
	    case OP_SAFE_C_QS:
	    case OP_SAFE_C_QQ:
	    case OP_SAFE_C_XXX:
	    case OP_SAFE_C_SSS:
	    case OP_SAFE_C_ALL_S:
	    case OP_SAFE_C_ALL_G:
	    case OP_SAFE_C_ALL_X:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      break;
	      
	      
	    case OP_SAFE_C_opCq:
	    case OP_SAFE_C_opQq: 
	    case OP_SAFE_C_opSq: 
	    case OP_SAFE_C_opSSq:
	    case OP_SAFE_C_opSCq:
	    case OP_SAFE_C_opSQq:
	    case OP_SAFE_C_opQSq:
	    case OP_SAFE_C_opCSq:
	    case OP_SAFE_C_opCq_S:
	    case OP_SAFE_C_opSq_S:
	    case OP_SAFE_C_opCq_C:
	    case OP_SAFE_C_opSq_C:
	    case OP_SAFE_C_opCSq_C:
	    case OP_SAFE_C_opSSq_C:
	    case OP_SAFE_C_opSCq_S:
	    case OP_SAFE_C_opSCq_C:
	    case OP_SAFE_C_opCSq_S:
	    case OP_SAFE_C_opSSq_S:
	    case OP_SAFE_C_opXXXq:
	    case OP_SAFE_C_Z:
	    case OP_SAFE_C_ZS:
	    case OP_SAFE_C_ZC:
	    case OP_SAFE_C_ZQ:
	    case OP_SAFE_C_ZXX:
	    case OP_SAFE_C_DXX:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, cadr(expr))) return(false);
	      break;
	      
	      
	    case OP_SAFE_C_C_opSq:
	    case OP_SAFE_C_C_opCq:
	    case OP_SAFE_C_C_opCSq:
	    case OP_SAFE_C_C_opSSq:
	    case OP_SAFE_C_S_opSq:
	    case OP_SAFE_C_S_opCq:
	    case OP_SAFE_C_S_opSCq:
	    case OP_SAFE_C_C_opSCq:
	    case OP_SAFE_C_S_opSSq:
	    case OP_SAFE_C_S_opCSq:
	    case OP_SAFE_C_SZ:
	    case OP_SAFE_C_CZ:
	    case OP_SAFE_C_QZ:
	    case OP_SAFE_C_XZX:
	    case OP_SAFE_C_XDX:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, caddr(expr))) return(false);
	      break;
	      
	      
	    case OP_SAFE_C_opCq_opCq:
	    case OP_SAFE_C_opSq_opSq:
	    case OP_SAFE_C_opSCq_opSCq:
	    case OP_SAFE_C_opSSq_opSSq:
	    case OP_SAFE_C_ZZ:
	    case OP_SAFE_C_ZZX:
	    case OP_SAFE_C_DD:
	    case OP_SAFE_C_DDX:
	      /* (op (op S) (op S)) */
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, cadr(expr))) return(false);
	      if (!c_function_is_ok(sc, caddr(expr))) return(false);
	      break;

	    case OP_SAFE_C_XXZ:
	    case OP_SAFE_C_XXD:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, cadddr(expr))) return(false);
	      break;

	    case OP_SAFE_C_XZZ:
	    case OP_SAFE_C_XDD:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, caddr(expr))) return(false);
	      if (!c_function_is_ok(sc, cadddr(expr))) return(false);
	      break;

	    case OP_SAFE_C_ZXZ:
	    case OP_SAFE_C_DXD:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, cadr(expr))) return(false);
	      if (!c_function_is_ok(sc, cadddr(expr))) return(false);
	      break;

	    case OP_SAFE_C_ZZZ:
	    case OP_SAFE_C_DDD:
	      if (!c_function_is_ok(sc, expr)) return(false);
	      if (!c_function_is_ok(sc, cadr(expr))) return(false);
	      if (!c_function_is_ok(sc, caddr(expr))) return(false);
	      if (!c_function_is_ok(sc, cadddr(expr))) return(false);
	      break;
	      
	    default:
	      return(false);
	    }
	}
    }
  return(true);
}


static bool is_hop_safe_closure(s7_pointer p)
{
  int op;
  op = optimize_data(p);
  return(((op & 1) == 1) &&
	 (op >= HOP_SAFE_THUNK) &&
	 (op <= HOP_SAFE_CLOSURE_opCq));
}


static bool form_is_safe(s7_scheme *sc, s7_pointer func, s7_pointer x, bool at_end, bool *bad_set)
{
  /* fprintf(stderr, "    f: %s %s %d %d %d\n", DISPLAY(func), DISPLAY_80(x), at_end, is_syntactic(car(x)), is_optimized(car(x))); */
  if (!is_proper_list(sc, x))
    return(false);
  if (is_syntactic(car(x)))
    {
      switch (syntax_opcode(car(x)))
	{
	case OP_OR:
	case OP_AND:
	case OP_BEGIN:
	  if (!body_is_safe(sc, func, cdr(x), at_end, bad_set))
	    return(false);
	  break;
	  
	case OP_QUOTE:
	  break;
	  
	  /* in the name binders, we first have to check that "func" actually is the same thing as the caller's func */
	case OP_LET:
	  if (is_symbol(cadr(x)))
	    {
	      s7_pointer vars, p;
	      if ((!is_pair(cddr(x))) ||
		  (!is_pair(cdddr(x)))) /* (let asdf) or possibly (let asdf ()) */
		return(false);
	      for (vars = caddr(x); is_pair(vars); vars = cdr(vars))
		{
		  if ((caar(vars) == func) ||
		      (caar(vars) == cadr(x)))
		    return(false); /* it's shadowed -- all bets are off! */
		}
	      
	      for (p = cdddr(x); is_not_null(p); p = cdr(p)); /* body might be empty: (define (f x) (let asd ())) */
	      if (is_pair(car(p)))
		{
		  form_is_safe(sc, cadr(x), car(p), true, bad_set);
		  if ((at_end) && (!*bad_set))
		    form_is_safe(sc, func, car(p), true, bad_set);
		}
	      return(false);
	    }
	  /* fall through */
	  
	case OP_LET_STAR:
	case OP_LETREC:
	  {
	    bool happy = true;
	    if (is_pair(cadr(x)))
	      {
		s7_pointer vars;
		for (vars = cadr(x); is_pair(vars); vars = cdr(vars))
		  {
		    if ((!is_pair(car(vars))) ||
			(!is_pair(cdar(vars))))
		      return(false);
		    
		    if ((is_pair(cadar(vars))) &&
			(!form_is_safe(sc, func, cadar(vars), false, bad_set)))
		      happy = false;
		    
		    if (caar(vars) == func)
		      return(false); /* it's shadowed */
		  }
	      }
	    if ((!body_is_safe(sc, func, cddr(x), at_end, bad_set)) ||
		(!happy))
	      return(false);
	  }
	  break;
	  
	case OP_IF:
	  {
	    bool t, e1, e2;
	    t = ((!is_pair(cadr(x))) || (form_is_safe(sc, func, cadr(x), false, bad_set)));
	    e1 = ((!is_pair(caddr(x))) || (form_is_safe(sc, func, caddr(x), at_end, bad_set)));
	    e2 = ((!is_pair(cdddr(x))) || (!is_pair(cadddr(x))) || (form_is_safe(sc, func, cadddr(x), at_end, bad_set)));
	    return((t) && (e1) && (e2));
	  }
	  break;
	  
	case OP_COND:
	  {
	    s7_pointer p;
	    bool happy = true;
	    for (p = cdr(x); is_pair(p); p = cdr(p))
	      {
		s7_pointer expr;
		expr = car(p);
		if (is_pair(expr)) /* ?? */
		  {
		    if ((is_pair(car(expr))) && (!form_is_safe(sc, func, car(expr), false, bad_set)))
		      happy = false;
		    if ((is_pair(cdr(expr))) && (!body_is_safe(sc, func, cdr(expr), at_end, bad_set)))
		      happy = false;
		  }
	      }
	    if ((!happy) || (is_not_null(p)))
	      return(false);
	  }
	  break;
	  
	case OP_CASE:
	  {
	    s7_pointer p;
	    bool happy = true;
	    if ((is_pair(cadr(x))) && (!form_is_safe(sc, func, cadr(x), false, bad_set))) return(false);
	    for (p = cddr(x); is_pair(p); p = cdr(p))
	      {
		if ((is_pair(car(p))) && (!body_is_safe(sc, func, cdar(p), at_end, bad_set)))
		  happy = false;
	      }
	    return(happy);
	  }
	  break;
	  
	case OP_DO:
	  /* (do (...) (...) ...) */
	  if (!is_pair(cddr(x)))
	    return(false);
	  if (!body_is_safe(sc, func, cdddr(x), false, bad_set))
	    return(false);
	  if (is_pair(cadr(x)))
	    {
	      s7_pointer vars;
	      for (vars = cadr(x); is_pair(vars); vars = cdr(vars))
		{
		  if (!is_pair(car(vars)))
		    return(false);
		  
		  if (caar(vars) == func)
		    return(false);
		  /* fprintf(stderr, "cadar: %s, cadr: %s\n", DISPLAY_80(cadar(vars)), DISPLAY_80(cadr(vars))); */
		  
		  if ((is_pair(cadar(vars))) &&
		      (!form_is_safe(sc, func, cadar(vars), false, bad_set)))
		    return(false);
		  
		  if ((is_pair(cddar(vars))) &&
		      (is_pair(caddar(vars))) &&
		      (!form_is_safe(sc, func, caddar(vars), false, bad_set)))
		    return(false);
		}
	    }
	  if ((is_pair(caddr(x))) &&
	      (!body_is_safe(sc, func, caddr(x), at_end, bad_set)))
	    return(false);
	  break;
	  
	case OP_SET:
	  /* if we set func, we have to make sure we abandon the tail call scan:
	   * (let () (define (hi a) (let ((v (vector 1 2 3))) (set! hi v) (hi a))) (hi 1))
	   */
	  /* fprintf(stderr, "%s: %s %d\n", DISPLAY(x), DISPLAY(func), cadr(x) == func); */
	  if (cadr(x) == func)
	    (*bad_set) = true;

	  /* PERHAPS: set pws if safe -- isn't this safe also? */
	  return(false);
	  
	default:
	  /* try to catch weird cases like: 
	   * (let () (define (hi1 a) (define (hi1 b) (+ b 1)) (hi1 a)) (hi1 1))
	   * (let () (define (hi1 a) (define (ho1 b) b) (define (hi1 b) (+ b 1)) (hi1 a)) (hi1 1))
	   */
	  /*
	    if (syntax_opcode(car(x)) >= OP_SAFE_IF1)
	    fprintf(stderr, "%s %s no good: %s, %s %s, %p %p%s\n", 
	    BOLD_TEXT, real_op_names[syntax_opcode(car(x))], DISPLAY(x), DISPLAY(func), DISPLAY(caadr(x)), caadr(x), func, UNBOLD_TEXT);
	  */
	  
	  if ((is_pair(cdr(x))) &&
	      ((cadr(x) == func) ||
	       ((is_pair(cadr(x))) && (caadr(x) == func))))
	    (*bad_set) = true;
	  return(false);
	}
    }
  else
    {
      if ((!is_optimized(x)) || 
	  ((is_unsafe(x)) &&
	   (!is_hop_safe_closure(x))))
	{
	  if (car(x) == func) /* try to catch tail call */
	    {
	      s7_pointer p;
	      for (p = cdr(x); is_pair(p); p = cdr(p))
		if ((is_pair(car(p))) &&
		    (!is_optimized(car(p))))
		  return(false);

	      if ((at_end) && (is_null(p) && (!*bad_set)))
		{
		  /* we still miss a few cases, and need more unknown optimizations, or set them here
		   *   (let () (define (hi1 a) (define (hi1 b) (+ b 1)) (hi1 a)) (hi1 1)) 2)
		   */
		  return(true);
		}
	      return(false);
	    }
	  
	  if (is_symbol(car(x)))
	    {
	      if (is_global(car(x)))
		{
		  s7_pointer f;
		  f = finder(sc, car(x));
		  if (((is_c_function(f)) &&
		       ((is_safe_procedure(f)) ||
			((is_null(cdddr(x))) &&
			 ((c_function_call(f) == g_member) || (c_function_call(f) == g_assoc))))) ||
		      ((is_closure(f)) &&
		       (is_safe_closure(closure_body(f)))))
		    {
		      s7_pointer p;
		      for (p = cdr(x); is_pair(p); p = cdr(p))
			if ((is_pair(car(p))) &&
			    ((!is_optimized(car(p))) ||
			     ((is_unsafe(car(p))) &&
			      (!is_hop_safe_closure(car(p))))))
			  {
			    /* fprintf(stderr, "unopt trailing arg: %s\n", DISPLAY(car(p))); */
			    if ((caar(p) != func) ||
				(!is_null(cdr(p))))
			      return(false);
			  }
		      return(is_null(p));
		    }
		}
	      else
		{
		  s7_pointer f;
		  f = find_symbol(sc, car(x));
		  if ((is_slot(f)) &&
		      ((is_syntax(slot_value(f))) || (is_any_macro(slot_value(f)))))
		    (*bad_set) = true;
		}
	    }
	  return(false);
	}
    }
  return(true);
}


static bool body_is_safe(s7_scheme *sc, s7_pointer func, s7_pointer body, bool at_end, bool *bad_set)
{
  s7_pointer p;
  /* fprintf(stderr, "b: %s %s %d\n", DISPLAY(func), DISPLAY_80(body), at_end); */
  for (p = body; is_pair(p); p = cdr(p))
    {
      if ((is_pair(car(p))) &&
	  (!form_is_safe(sc, func, car(p), (at_end) && (is_null(cdr(p))), bad_set)))
	{
	  if ((at_end) && (!is_null(cdr(p))) && (!*bad_set))
	    {
	      /* we checked in check_define that this is not a circular list */
	      for (p = cdr(p); is_not_null(cdr(p)); p = cdr(p));
	      if (is_pair(car(p)))
		form_is_safe(sc, func, car(p), at_end, bad_set);
	    }
	  return(false);
	}
    }
  return(true);
}
