		      case T_VECTOR:
			set_optimize_data(code, OP_VECTOR_C);
			ecdr(code) = f;
			goto OPT_EVAL;
			
		      case T_C_FUNCTION:
		      case T_C_ANY_ARGS_FUNCTION:
		      case T_C_OPT_ARGS_FUNCTION:
		      case T_C_RST_ARGS_FUNCTION:
