s7_pointer s7_make_integer(s7_scheme *sc, s7_Int n) 
{
  s7_pointer x;

  if ((n & ~(NUM_SMALL_INTS - 1)) == 0)            /* ((n >= 0) && (n < NUM_SMALL_INTS)) is slower */
    return(small_int(n));

  NEW_CELL(sc, x);
  set_type(x, T_INTEGER);
  integer(x) = n;

  return(x);
}


#if WITH_GCC
static s7_pointer make_integer_1(s7_scheme *sc, s7_Int n)
{
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_INTEGER);
  integer(x) = n;
  return(x);
}

#define make_integer(Sc, N) ({ s7_Int _N_; _N_ = N; (((_N_ & ~(NUM_SMALL_INTS - 1)) == 0) ? small_int(_N_) : make_integer_1(Sc, _N_)); })
#else
#define make_integer(Sc, N) s7_make_integer(Sc, N)
#endif


static s7_pointer make_mutable_integer(s7_scheme *sc, s7_Int n)
{
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_INTEGER);
  integer(x) = n;
  return(x);
}


static s7_pointer make_permanent_integer(s7_Int i)
{
  s7_pointer p;
  p = (s7_pointer)calloc(1, sizeof(s7_cell));
  typeflag(p) = T_IMMUTABLE | T_INTEGER;
  p->hloc = NOT_IN_HEAP;
  integer(p) = i;
  return(p);
}


s7_pointer s7_make_real(s7_scheme *sc, s7_Double n) 
{
  s7_pointer x;

  if (n == 0.0)
    return(real_zero);

  NEW_CELL(sc, x);
  set_type(x, T_REAL);
  real(x) = n;
  
  return(x);
}


static s7_pointer make_permanent_real(s7_Double n) 
{
  s7_pointer x;
  x = (s7_pointer)calloc(1, sizeof(s7_cell));
  set_type(x, T_IMMUTABLE | T_REAL);
  x->hloc = NOT_IN_HEAP;
  real(x) = n;
  return(x);
}


s7_pointer s7_remake_real(s7_scheme *sc, s7_pointer rl, s7_Double n) 
{
  if ((rl != real_zero) &&
      (type(rl) > T_RATIO))
    real(rl) = n;
  else rl = s7_make_real(sc, n);
  return(rl);
}


s7_pointer s7_make_complex(s7_scheme *sc, s7_Double a, s7_Double b)
{
  s7_pointer x;
  NEW_CELL(sc, x);
  if (b == 0.0)
    {
      set_type(x, T_REAL);
      real(x) = a;
    }
  else
    {
      set_type(x, T_COMPLEX);
      real_part(x) = a;
      imag_part(x) = b;
    }
  return(x);
}


s7_pointer s7_make_ratio(s7_scheme *sc, s7_Int a, s7_Int b)
{
  s7_pointer x;
  s7_Int divisor;

  if (b == 0)
    return(division_by_zero_error(sc, "make-ratio", list_2(sc, make_integer(sc, a), small_int(0))));
  if (a == 0)
    return(small_int(0));
  if (b == 1)
    return(make_integer(sc, a));

#if (!WITH_GMP)
  if (b == S7_LLONG_MIN)
    {
      if (a == b)
	return(small_int(1));

      /* we've got a problem... This should not trigger an error during reading -- we might have the
       *   ratio on a switch with-bignums or whatever, so its mere occurrence is just an annoyance.
       *   We'll try to do something...
       */
      if (a & 1)
	{
	  if (a == 1)
	    return(real_NaN);
	  /* not an error here because 1/9223372036854775808 might be in a block of unevaluated code */
	  b = b + 1;
	}
      else
	{
	  a /= 2;
	  b /= 2;
	}
    }
#endif

  if (b < 0)
    {
      a = -a;
      b = -b;
    }
  divisor = c_gcd(a, b);
  if (divisor != 1)
    {
      a /= divisor;
      b /= divisor;
    }
  if (b == 1)
    return(make_integer(sc, a));
  
  NEW_CELL(sc, x);
  set_type(x, T_RATIO);
  numerator(x) = a;
  denominator(x) = b;

  return(x);
}



static s7_pointer exact_to_inexact(s7_scheme *sc, s7_pointer x)
{
  /* this is tricky because a big int can mess up when turned into a double:
   *   (truncate (exact->inexact most-positive-fixnum)) -> -9223372036854775808
   */
  switch (type(x))
    {
    case T_INTEGER:
      return(s7_make_real(sc, (s7_Double)(integer(x))));

    case T_RATIO:
      return(s7_make_real(sc, (s7_Double)(fraction(x))));

    case T_REAL:
    case T_COMPLEX:              /* apparently (exact->inexact 1+i) is not an error */
      return(x);

    default:
      return(s7_wrong_type_arg_error(sc, "exact->inexact", 0, x, "a number"));
    }
}



static double default_rationalize_error = 1.0e-12;

static s7_pointer inexact_to_exact(s7_scheme *sc, s7_pointer x)
{
  switch (type(x))
    {
    case T_INTEGER:
    case T_RATIO:
      return(x);

    case T_REAL:
      {
	s7_Int numer = 0, denom = 1;
	s7_Double val;

	val = s7_real(x);
	if ((isinf(val)) || (is_NaN(val)))
	  return(s7_wrong_type_arg_error(sc, "inexact->exact", 0, x, "a normal real"));

	if ((val > S7_LLONG_MAX) ||
	    (val < S7_LLONG_MIN))
	  return(s7_out_of_range_error(sc, "inexact->exact", 0, x, "too large to express as an integer"));

	if (c_rationalize(val, default_rationalize_error, &numer, &denom))
	  return(s7_make_ratio(sc, numer, denom));
      }

    default:
      return(s7_wrong_type_arg_error(sc, "inexact->exact", 0, x, "a real"));
    }

  return(x);
}


