static s7_pointer not_c_c;
static s7_pointer g_not_c_c(s7_scheme *sc, s7_pointer args) 
{
  /* args: ( (null? l) ) */
  return(make_boolean(sc, is_false(sc, c_call(car(args))(sc, cdar(args)))));
}

static s7_pointer not_chooser(s7_scheme *sc, s7_pointer g, int args, s7_pointer expr)
{
  if (is_optimized(cadr(expr))) /* cadr(expr) might be a symbol, for example; is_optimized includes is_pair */
    {
      if (optimize_data(cadr(expr)) == HOP_SAFE_IS_PAIR_S)
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(not_is_pair);
	}
      if (optimize_data(cadr(expr)) == HOP_SAFE_IS_SYMBOL_S)
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(not_is_symbol);
	}

      if (optimize_data(cadr(expr)) == HOP_SAFE_C_S)
	{
	  s7_function f;
	  f = c_call(cadr(expr));
	  
	  if (f == g_is_pair)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_pair);
	    }
	  if (f == g_is_null)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_null);
	    }
	  if (f == g_is_symbol)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_symbol);
	    }
	  if (f == g_is_list)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_list);
	    }
	  if ((f == g_is_number) || (f == g_is_complex))
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_number);
	    }
	  if (f == g_is_rational)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_rational);
	    }
	  if (f == g_is_real)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_real);
	    }
	  if (f == g_is_integer)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_integer);
	    }
	  if (f == g_is_char)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_char);
	    }
	  if (f == g_is_boolean)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_boolean);
	    }
	  if (f == g_is_string)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_string);
	    }
	  if (f == g_is_eof_object)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_eof);
	    }
	}

      if ((optimize_data(cadr(expr)) == HOP_SAFE_C_SQ) &&
	  (c_call(cadr(expr)) == g_is_eq))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(not_is_eq_sq);
	}

      if (optimize_data(cadr(expr)) == HOP_SAFE_C_SS)
	{
	  if (c_call(cadr(expr)) == g_is_eq)
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(not_is_eq_ss);
	    }
	}

      if (optimize_data(cadr(expr)) == HOP_SAFE_C_C)
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  if (c_call(cadr(expr)) == g_is_pair_car)
	    return(not_is_pair_car);
	  if (c_call(cadr(expr)) == g_is_null_cdr)
	    return(not_is_null_cdr);
	  return(not_c_c);
	}
    }

  if ((is_symbol(cadr(expr))) &&
      (optimize_data(expr) == HOP_SAFE_C_S))
    {
      optimize_data(expr) = HOP_SAFE_NOT_S;
    }
  return(g);
}


static s7_pointer vector_ref_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if (args == 2)
    {
      if (is_symbol(cadr(expr)))
	{
	  if ((s7_is_integer(caddr(expr))) &&
	      (s7_integer(caddr(expr)) >= 0))
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(vector_ref_ic);
	    }

	  if ((is_pair(caddr(expr))) &&
	      (is_optimized(caddr(expr))) &&
	      (c_call(caddr(expr)) == g_add_cs1))
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(vector_ref_add1);
	      /* vector_ref_sub1 was not worth the code, and few other easily optimized expressions happen here */
	    }
	}
      return(vector_ref_2);
    }
  return(f);
}


static s7_pointer vector_set_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if (args == 3)
    {
      if ((s7_is_integer(caddr(expr))) &&
	  (s7_integer(caddr(expr)) >= 0) &&
	  (is_symbol(cadr(expr))) &&
	  (is_symbol(cadddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(vector_set_ic);
	}
      if ((is_symbol(cadr(expr))) &&
	  (is_symbol(caddr(expr))) &&
	  (!is_pair(cadddr(expr))) &&
	  (!is_symbol(cadddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(vector_set_ssc);
	}
      if ((is_symbol(cadr(expr))) &&
	  (is_symbol(caddr(expr))) &&
	  (is_pair(cadddr(expr))) &&
	  (is_optimized(cadddr(expr))))
	{
	  s7_pointer vref;
	  vref = cadddr(expr);
	  if ((is_symbol(cadr(vref))) &&
	      (caddr(vref) == caddr(expr)) &&
	      (c_call(vref) == g_vector_ref_2))
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(vector_set_ref);
	    }
	}
      return(vector_set_3);
    }
  return(f);
}


static s7_pointer list_set_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if ((args == 3) &&
      (s7_is_integer(caddr(expr))) &&
      (s7_integer(caddr(expr)) >= 0) &&
      (s7_integer(caddr(expr)) < MAX_LIST_LENGTH))
    return(list_set_ic);
  return(f);
}


static s7_pointer hash_table_ref_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if (args == 2)
    {
      if ((is_symbol(cadr(expr))) &&
	  (is_symbol(caddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(hash_table_ref_ss);
	}
      if ((is_symbol(cadr(expr))) &&
	  (is_pair(caddr(expr))) &&
	  (is_optimized(caddr(expr))) &&
	  (optimize_data(caddr(expr)) == HOP_SAFE_C_S) &&
	  (c_call(caddr(expr)) == g_car))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(hash_table_ref_car);
	}
      return(hash_table_ref_2);
    }
  return(f);
}


static s7_pointer add_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  /* fprintf(stderr, "add %d %s\n", args, DISPLAY(expr)); */
  /* (+ s f) (+ (* s s) s) (+ s s) (+ s (* s s))
   */
  if (args == 1)
    return(add_1);

  if (args == 2)
    {
      if (cadr(expr) == small_int(1))
	return(add_1s);
      if (caddr(expr) == small_int(1))
	{
	  if (is_symbol(cadr(expr)))
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(add_cs1);
	    }
	  return(add_s1);
	}

      if ((s7_is_integer(caddr(expr))) &&
	  (is_symbol(cadr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(add_si);
	}

      if ((s7_is_integer(cadr(expr))) &&
	  (is_symbol(caddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(add_is);
	}

      if ((type(caddr(expr)) == T_REAL) &&
	  (is_symbol(cadr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(add_sf);
	}

      if ((type(cadr(expr)) == T_REAL) &&
	  (is_symbol(caddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(add_fs);
	}

#if 0
      /* in snd-test, apparently (+ int (* int int)) is very common, but I think it is fake: (sound-data-set! sd chn i (+ i (* chn 10))) and so on
       */
      if ((!is_pair(cadr(expr))) &&
	  (is_pair(caddr(expr))) &&
	  (is_optimized(caddr(expr))) &&
	  (optimize_data(caddr(expr)) == HOP_SAFE_C_C) &&
	  ((c_call(caddr(expr)) == g_multiply_is) || (c_call(caddr(expr)) == g_multiply_si)))
	{
	  fprintf(stderr, "add2 si + p %s: %s\n", (is_optimized(caddr(expr))) ? opt_names[optimize_data(caddr(expr))] : "unopt", DISPLAY_80(expr)); 
	}
#endif

      return(add_2);
    }

  return(f);
}


static s7_pointer multiply_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  /* it's possible to precompute constant multiplies, use OP_CONSTANT as the optimize_data, 
   *    put the value in fcdr, remove it from the heap, return NULL (checking for ecdr null in 
   *    set_c_function and c_function_is_ok), then simply return fcdr in OP_CONSTANT, but
   *    that sort of thing almost never happens in real code.
   */
  if (args == 2)
    {
      if ((is_symbol(cadr(expr))) &&
	  (s7_is_integer(caddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(multiply_si);
	}

      if ((is_symbol(caddr(expr))) &&
	  (s7_is_integer(cadr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(multiply_is);
	}

      if ((type(cadr(expr)) == T_REAL) &&
	  (is_symbol(caddr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(multiply_fs);
	}
      if ((type(caddr(expr)) == T_REAL) &&
	  (is_symbol(cadr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(multiply_sf);
	}
#if (!WITH_GMP)
      if ((is_symbol(cadr(expr))) &&
	  (cadr(expr) == caddr(expr)))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(sqr_ss);
	}
#endif
      /* fprintf(stderr, "mul2: %s\n", DISPLAY_80(expr)); */

      /* TODO: in clm, gen * gen of any sort is real * real: (* (env ampf) (polywave gen1 (+ (env frqf) ...)))
       *       or even (* mx (polynomial pcoeffs (* amp sig)))
       *       how to handle constants? (* 0.25 3030)
       *       (* r (sin mx)) (* 0.5 (+ x0 x1)) (* x2 (+ 38.027264 x2))
       *     !!  (* (env indf1) (oscil fmosc1 (+ (* fm1-rat vib) fuzz)))
       *       and here one is known to be int: (* ak (floor (/ r ak))) (* 1.25 (frames))
       */
      return(multiply_2);
    }
  return(f);
}

/* (define (hi a) (- a 1.0)) (hi (string->number "#e1."))
 */

static s7_pointer subtract_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if (args == 1)
    return(subtract_1);

  if (args == 2)
    {
      if (caddr(expr) == small_int(1))
	{
	  if (is_symbol(cadr(expr)))
	    {
	      /* fprintf(stderr, "subtract_cs1: %s\n", DISPLAY(expr)); */
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(subtract_cs1);
	    }
	  /* fprintf(stderr, "subtract_s1: %s\n", DISPLAY(expr));  */
	  return(subtract_s1);
	}
      if ((s7_is_integer(caddr(expr))) &&
	  (is_symbol(cadr(expr))))
	{
	  optimize_data(expr) = HOP_SAFE_C_C;
	  return(subtract_csn);
	}
      return(subtract_2);
    }
  return(f);
}


static s7_pointer equal_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if (args == 2)
    {
      if (s7_is_integer(caddr(expr)))
	{
	  if (is_optimized(cadr(expr)))
	    {
	      s7_function f;
	      f = c_call(cadr(expr));
	      if (f == g_length)
		{
		  if (optimize_data(cadr(expr)) == HOP_SAFE_C_S)
		    {
		      optimize_data(expr) = HOP_SAFE_C_C;
		      return(equal_length_ic);
		    }
		}
	    }
	  if (is_symbol(cadr(expr)))
	    {
	      optimize_data(expr) = HOP_SAFE_C_C;
	      return(equal_s_ic);
	    }
	}
      /* fprintf(stderr, "=2: %s\n", DISPLAY_80(expr)); */
      return(equal_2);
    }
  return(f);
}


