static Xen C_to_Xen_Widgets(Widget *array, int len)
{
  Xen lst = Xen_empty_list;
  if (array)
    {
      int i, loc;
      loc = xm_protect(lst);
      for (i = len - 1; i >= 0; i--)
	lst = Xen_cons(C_to_Xen_Widget(array[i]), lst);
      xm_unprotect_at(loc);
    }
  return(lst);
}

