static int Xen_to_C_INT_DEF(Xen len, Xen lst) 
{
  if (Xen_is_integer(len))
    return(Xen_integer_to_C_int(len));
  else
    {
      int list_len;
      list_len = Xen_list_length(lst);
      return((int)(list_len / 2));
    }
}

